//Function to handle Chv-Tstat-FCU base events.
 void _chvStatFcu_BaseEvent(GenericBase device, BaseEventArgs args)
 {
   if (args.EventId == ChvTstatFcu.Auto1ptSetpointLimitsDisableEventId)
    {
     CrestronConsole.PrintLine("{0} Auto1ptSpLimits Disable event occurred for system configuration.\r\n", myChvTstatFcu.Auto1ptSetpointLimitsDisableFeedback);
    }
 }

//Function to handle climate control component call change events.
 void _chvStatFcu_ClimateControlComponentCallChange(Thermostat paramThermostat, ClimateControlCallEventArgs paramClimateControlCallEventArgs)
 {
   if (paramClimateControlCallEventArgs.ClimateControlComponent.ComponentState == eClimateControlComponentState.Active)
   {
     CrestronConsole.PrintLine("Climate control component state is {0} and Stage is {1} and ComponentType {2} .\r\n", paramClimateControlCallEventArgs.ClimateControlComponent.ComponentState,
               paramClimateControlCallEventArgs.ClimateControlComponent.Stage, paramClimateControlCallEventArgs.ClimateControlComponent.ComponentType);
   }
 }

//Function to handle fancall events.
 void _chvStatFcu_FanCallChange(Thermostat paramThermostat, FanCallEventArgs paramFanEventArg)
 {
   if (paramFanEventArg.ThermostatFanSpeed == eFanSpeed.High)
   {
     CrestronConsole.PrintLine("FanCallChange eFanState is {0}.\r\n", paramFanEventArg.ThermostatFanSpeed);
   }
 }

//Function to handle humidity change events.
 void _chvStatFcu_HumidityEvent(Thermostat paramThermostat, HumidityEventArgs paramHumidityEventArgs)
 {
   if (paramHumidityEventArgs.humidityType == eHumidityType.LocalHumidity)
   {
    CrestronConsole.PrintLine("Humidity type is  :{0}.\r\n", paramHumidityEventArgs.humidityType);
   }
 }

//Function to handle temperature type change events.
 void _chvStatFcu_TemperatureChange(Thermostat paramThermostat, TemperatureEventArgs paramTemperatureEventArgs)
 {
   if (paramTemperatureEventArgs.temperatureType == eTemperatureType.LocalTemperature)
   {
    CrestronConsole.PrintLine("Temperature type is  :{0}.\r\n", paramTemperatureEventArgs.temperatureType);
   }
 }

