    Din1Tstat8 _myDin1Tstat8;

    public ControlSystemDin1Tstat8()
    {
        //Register console commands
        CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is an DIN-1TSTAT8 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
        CrestronConsole.AddNewConsoleCommand(SetSetPointCmd, "SetSetPoint", "This is an DIN-1TSTAT8 SetSetPoint command", ConsoleAccessLevelEnum.AccessOperator);
    }

    // Method to create the device object
    public void CreateDeviceCmd(String args)
    {
        _myDin1Tstat8 = new Din1Tstat8(0x25, this);
        _myDin1Tstat8.Register();

        _myDin1Tstat8.BaseEvent += _din1Tstat8_BaseEvent;
    }

    //Event handler for the top level slot join changes
    public void _din1Tstat8_BaseEvent(GenericBase device, BaseEventArgs args)
    {
        if (args.EventId == Din1Tstat8.ModeOffFeedbackEventId)
        {
            CrestronConsole.PrintLine("Mode Off Feedback event occurred {0}.\r\n", _myDin1Tstat8.Channel[(uint)args.Index].ModeOffFeedback);
        }
        else if (args.EventId == Din1Tstat8.TemperatureFeedbackEventId)
        {
            CrestronConsole.PrintLine("Temperature Feedback event occurred {0} and index is {1}.\r\n", _myDin1Tstat8.Channel[(uint)args.Index].TemperatureFeedback.UShortValue, args.Index);
        }
    }

    //Method to set the set point value
    public void SetSetPointCmd(String args)
    {
        var stra = args.Split(' ');

        var index = Convert.ToUInt16(stra[0]);
        var value = Convert.ToUInt16(stra[1]);
        _myDin1Tstat8.Channel[index].SetPoint.UShortValue = value;
    }
}

 public static class ChvTstat_Example


 private static CenRfgwEx _rfGateway; //CHANGE TO WHATEVER GATEWAY YOU ARE USING.
 private static ChvTstatex _therm;


 // Helper function to initialize the RF thermostat object and set up all initial values
 // for the RFThermostat.
 // IMPORTANT: Register your rfGateway after you initialize all your object and object attributes. 
 // Can lead to issues with device registration if not done correctly.
 // You can call this function in your InitializeDevice function within your program 
 // and pass your global objects into this function to initialize your RFThermostat. 
 public static bool InitializeDevice(ControlSystem controller)
 {
 	bool returnValue = false;

 	try
 	{
 		// If the control system does not support Ethernet, then we cannot connect to the gateway. So we return.
 		if (!controller.SupportsEthernet)
 			return returnValue; 

 		// If the controller is null then also return right away.
 		if (controller == null)
 			return returnValue;


 		// Create a new object for the rfGateway connected to the Control System. ->(this)
 		// Device is connected the control system by an IP_ID. 
 		// In this example we bind it to IP_ID 4.
 		// However, yours can be on another so feel free to change this value. 
 		// The second parameter, "this" is referring to the ControlSystem class that extends the CrestronControlSystem.
 		// If you are not within the scope of such a class, make sure you have access to it. 
 		_rfGateway = new CenRfgwEx(0x04, controller);

 		// Initialize your thermostat object with it's rf_id and RF-Gateway device.
 		_therm = new ChvTstatex(0x03, rfGateway);


 		// Assign the dimmer, and rfGateway a description.
 		// These descriptions will be used within the IP table constructed by the program on the control system.
 		_rfGateway.Description = "GWEXER Main";

 		// Set a description for your device.
 		_therm.Description = "Kitchen thermostat";

 		// Allow the device to output sensor data about itself.
 		_therm.EnableSensorOutputs.BoolValue = true;

 		// Set up event listeners for the thermostat.
 		EventHandlerInitialization();

 		// Register the gateway device to the RMC3 controller.
 		if (_rfGateway.Register() != eDeviceRegistrationUnRegistrationResponse.Success)
 		{
 			ErrorLog.Error("rf-Gateway cannot be registered: {0}", rfGateway.RegistrationFailureReason.ToString());
 		}
 		else
 		{
 			// If rfGateway registration goes successfully then we can return successfully.
			 	returnValue = true;
 		}

 	}
 	catch (Exception e)
 	{
 		// If anything has an issue, this exception will trigger and be logged within the device's error log.
 		ErrorLog.Error("Error Registering RF Devices: {0}", e.Message.ToString());

 		// Mark our return value for failure.
 		returnValue = false;
		}
 	// Finally return an indication of the success of the function execution.
 	return returnValue;
 }


 // Helper to initialize all thermostat event handlers.
 private static void EventHandlerInitialization()
 {
     // Check if the gateway is null first.
     if(_rfGateway == null){
         ErrorLog.Error("Could not set up Gateway event handlers, object is null");
         throw new NullReferenceException();
         return;
     }

 	// Listen for a change in the rfGateway's online status.
 	_rfGateway.OnlineStatusChange += new OnlineStatusChangeEventHandler(OnlineStatusChange);

 	// Online status change event handler.
 	_therm.OnlineStatusChange += new OnlineStatusChangeEventHandler(Device_OnlineStatusChange);

 	// Listen for any temp changes from the thermostat.
 	_therm.TemperatureChange += new TemperatureChangeHandler(therm_TemperatureChange);

 	// Climate control event handler.
 	_therm.ClimateControlComponentCallChange += new ClimateControlComponentCallChangeHandler(therm_ClimateControlComponentCallChange);
 }

 // Sets the thermostat's mode to cooling. 
 public static void SetThermostatCool()
 {
 	_therm.SystemMode = eSystemMode.Cool;
 }

 // Sets the thermostat's mode to heating.
 public static void SetThermostatHeat()
 {
 	_therm.SystemMode = eSystemMode.Heat;
 }

 // Sets the thermostat's mode to off.
 public static void SetThermostatOff()
 {
 	_therm.SystemMode = eSystemMode.Off;
 }

 // Raises the temperature point for whatever mode the thermostat is in currently.
 public static void RaiseSetPoint(short temp)
 {
 	if (_therm.SystemModeFeedback == eSystemMode.Cool)
 	{
 		// Check to ensure we are below the max heat temp.
 		if (_therm.MaxCoolSetpointFeedback.ShortValue > _therm.CoolSetpointFeedback.ShortValue)
 		{
 			_therm.CoolSetpoint.ShortValue += temp;
			}
 		else
 		{
 			ErrorLog.Notice("Could not raise temp.");
 		}
 	}
 	else if (therm.SystemModeFeedback == eSystemMode.Heat)
 	{
 		// Check to ensure we are below the max cool temp.
 		if (_therm.MaxHeatSetpointFeedback.ShortValue > _therm.HeatSetpointFeedback.ShortValue)
 		{
 			therm.HeatSetpoint.ShortValue += temp;
 		}
 		else
 		{
 			ErrorLog.Notice("Could not raise temp.");
 		}
 	}
 }

 // Lowers the temperature point for whatever mode the thermostat is in currently.
 public static void LowerSetPoint(short temp)
 {
 	if (_therm.SystemModeFeedback == eSystemMode.Cool)
 	{
 		//Check to ensure we are below the max heat temp.
 		if (_therm.CoolSetpointFeedback.UShortValue > therm.MinCoolSetpointFeedback.UShortValue)
 		{
 			therm.CoolSetpoint.ShortValue -= temp;
 		}
 		else
 		{
 			ErrorLog.Notice("Could not lower temp.");
 		}
 		}
 	else if (_therm.SystemModeFeedback == eSystemMode.Heat)
 	{
 		// Check to ensure we are below the max cool temp.
 		if (_therm.HeatSetpointFeedback.UShortValue > _therm.MinHeatSetpointFeedback.UShortValue)
 		{
 			therm.HeatSetpoint.ShortValue -= temp;
 		}
 		else
 		{
 			ErrorLog.Notice("Could not lower temp.");
 		}
 	}
 }

 // Sets the temperature point for AutoP1.
 public static void AutoP1SetPoint(ushort temp)
 {
 	_therm.Auto1ptSetpoint.UShortValue = temp;
 }

 // Event handler for all online status changes.
 //When a device goes online or offline this event handler will listen for it.
 private static void Device_OnlineStatusChange(GenericBase currentDevice, OnlineOfflineEventArgs args)
 {
 	if (args.DeviceOnLine)
 		ErrorLog.Notice("{0} is online", currentDevice.Description);
 	else
 		ErrorLog.Notice("{0} is offline", currentDevice.Description);
 }

 // Handles all temp change events coming from the device. 
 private static void therm_TemperatureChange(Thermostat paramThermostat, TemperatureEventArgs paramTemperatureEventArgs)
 {
 	ErrorLog.Notice("New temperature: {0}", paramTemperatureEventArgs.newTemperature);
 }

 //Handles all climate control component event changes coming from the device.
 private static void therm_ClimateControlComponentCallChange(Thermostat paramThermostat, ClimateControlCallEventArgs paramClimateControlCallEventArgs)
 {
 	ErrorLog.Notice("Current therm state: {0}", paramClimateControlCallEventArgs.ClimateControlComponent.ComponentState.ToString());
 }
