static Ts1542C _ts1542c;
static int _count;

public ControlSystemTs1542()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(AllAudioOnCmd, "AllAudioOn", "This is a Ts1542 AllAudioOn command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a Ts1542 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

// Sig callback used to read Audio Reserved Sig information received from the Touch Panel.
static void ExtenderAudioReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    CrestronConsole.PrintLine("Audio Reserved Sig {0}", args.Sig.Number);

    if (args.Sig == _ts1542c.ExtenderAudioReservedSigs.AllAudioVolumeFeedback)
    {
        CrestronConsole.PrintLine("AllAudioVolumeFeedback: {0}", args.Sig.UShortValue);
    }
}


// Method to create TS 1542C device        
public void CreateDeviceCmd(String args)
{
    //Initialize the TS 1542C device
    _ts1542c = new Ts1542C(0x16, this);

    _ts1542c.ExtenderAudioReservedSigs.Use();
    _ts1542c.ExtenderEthernetReservedSigs.Use();
    _ts1542c.ExtenderSystemReservedSigs.Use();
    _ts1542c.ExtenderSystem3ReservedSigs.Use();
    _ts1542c.ExtenderVoipReservedSigs.Use();
    _ts1542c.ExtenderVoiceControlReservedSigs.Use();
    _ts1542c.ExtenderScreenSaverReservedSigs.Use();
    _ts1542c.ExtenderApplicationControlReservedSigs.Use();
    _ts1542c.ExtenderSystem2ReservedSigs.Use();
    _ts1542c.ExtenderAuthenticationReservedSigs.Use();
    _ts1542c.ExtenderAnnotationReservedSigs.Use();

    //Register the TS 1542C with the Control system
    //This will enable the gateway connection ("127.0.0.1") in the system at the 
    //ID specified above that the TS 1542 will connect to.
    _ts1542c.Register();

    _ts1542c.SigChange += Ts1542CSigChange;

    //Reserved sig handler initialization
    _ts1542c.ExtenderAudioReservedSigs.DeviceExtenderSigChange += ExtenderAudioReservedSigs_DeviceExtenderSigChange;
}

// Method to set the Audio to ON state
public void AllAudioOnCmd(String args)
{
    CrestronConsole.ConsoleCommandResponse("Enable Audio\r\n");
    _ts1542c.ExtenderAudioReservedSigs.AllAudioOn();
}

// Sig callback used to read information received from the TS 1542C.
static void Ts1542CSigChange(GenericBase currentDevice, SigEventArgs args)
{
    if (args.Event == eSigEvent.BoolChange)
    {
        var tempBooleanSig = args.Sig as BoolOutputSig;

        if (tempBooleanSig != null)
        {
            if (tempBooleanSig.Number <= 16000)
            {
                //This means Boolean "cue" changed on the TS 1542C
                //Just print some information for now.
                CrestronConsole.PrintLine("Boolean Sig {0}'s state {1}", tempBooleanSig.Number, tempBooleanSig.BoolValue);

                _ts1542c.StringInput[tempBooleanSig.Number].StringValue = string.Format("Press {0} {1}", tempBooleanSig.Number, _count++);

                _ts1542c.BooleanInput[50].BoolValue = true;
            }
            else
            {
                CrestronConsole.PrintLine("Boolean Sig {0} is not processed", tempBooleanSig.Number);
            }
        }
    }
    else if (args.Event == eSigEvent.UShortChange)
    {
        var tempIntegerSig = args.Sig as UShortOutputSig;

        if (tempIntegerSig != null)
        {
            if (tempIntegerSig.Number <= 16000)
            {
                //This means Integer "cue" changed on the _ts1542c
                //Just print some information for now.
                CrestronConsole.PrintLine("Integer Sig {0}'s state {1}", tempIntegerSig.Number, tempIntegerSig.UShortValue);

                _ts1542c.UShortInput[tempIntegerSig.Number].UShortValue = tempIntegerSig.UShortValue;
            }
            else
            {
                CrestronConsole.PrintLine("Integer Sig {0} is not processed", tempIntegerSig.Number);
            }
        }
    }
    else if (args.Event == eSigEvent.StringChange)
    {
        var tempStringSig = args.Sig as StringOutputSig;

        if (tempStringSig != null)
        {
            if (tempStringSig.Number <= 16000)
            {
                //This means String "cue" changed on the TS 1542C
                //Just print some information for now.
                CrestronConsole.PrintLine("String Sig {0}'s state {1}", tempStringSig.Number, tempStringSig.StringValue);

                _ts1542c.StringInput[tempStringSig.Number].StringValue = tempStringSig.StringValue;
            }
            else
            {
                CrestronConsole.PrintLine("String Sig {0} is not processed", tempStringSig.Number);
            }
        }
    }   
}

