public class ControlSystemTswxx70 : CrestronControlSystem
{

    public ControlSystemTswxx70()
    {
        //Register console commands 
        CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a Tswxx70 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    }

    public void CreateDeviceCmd(String args)
    {
        uint extenderSlotCount = 1;

        //Initialize the Tsw570 device
        _tswxx70 = new Tsw570(0x11, this);


        CrestronConsole.PrintLine("Inizializing {0} extenders", _tswxx70.Name);
        try
        {
            if (_tswxx70.ExtenderApplicationControlReservedSigs != null)
            {
                _tswxx70.ExtenderApplicationControlReservedSigs.Use();
                _tswxx70.ExtenderApplicationControlReservedSigs.DeviceExtenderSigChange += ExtenderApplicationControlReservedSigs_DeviceExtenderSigChange;
            }
        }
        catch (Exception e)
        {
            CrestronConsole.PrintLine("\tException {0} occured", e.Message);
        }

        //Register the Tswxx70 with the Control System
        //This will enable the gateway connection ("127.0.0.1") in the system at the 
        //ID specified above that the Tswxx60 will connect to.
        _tswxx70.Register();

        _tswxx70.SigChange += Tswxx70ObjSigChange;
        _tswxx70.ButtonStateChange += Tswxx70ObjButtonChange;

        if (_tswxx70.Registered == true)
        {
            CrestronConsole.PrintLine("{0} device registered successfully.", _tswxx70);
        }
        else
        {
            CrestronConsole.PrintLine("{0} device registeration failed.", _tswxx70);
        }
    }

    static void ExtenderApplicationControlReservedSigs_DeviceExtenderSigChange(DeviceExtender currentdeviceextender, SigEventArgs args)
    {
        CrestronConsole.PrintLine("Application Control Reserved Sig {0}", args.Sig.Number);

        //Sig change for the extender
        switch (args.Sig.Type)
        {
            case eSigType.Bool:
                {
                    if (args.Sig.Number == _tswxx70.ExtenderApplicationControlReservedSigs.HideOpenApplicationFeedback.Number)
                    {
                        CrestronConsole.PrintLine("HideOpenApplicationFeedback: {0}", args.Sig.BoolValue);
                    }
                    break;
                }
        }
    }
}

static Dge100 _dge100;

   public ControlSystemDge100()
   {
       //Register console commands
       CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DGE-100 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(AllAudioToggleCmd, "AllAudioToggle", "This is a DGE-100 AllAudioToggle command", ConsoleAccessLevelEnum.AccessOperator);
   }

   public void CreateDeviceCmd(String args)
   {
       //Initialize the DGE-100 device for Ethernet Communication.
       _dge100 = new Dge100(0x18, this);

       _dge100.ExtenderAuthenticationReservedSigs.Use();

       _dge100.BaseEvent += Dge100EventHandler;

       _dge100.HdmiIn.VideoAttributes.AttributeChange += Dge100_VideoAttributeEventHandler;
       _dge100.HdmiIn.StreamCec.CecChange += Dge100CecChangeEventHandler;
       _dge100.HdmiIn.StreamChange += Dge100_StreamChangeEventHandler;

       _dge100.HdmiOut.VideoAttributes.AttributeChange += Dge100_VideoAttributeEventHandler;
       _dge100.HdmiOut.StreamCec.CecChange += Dge100CecChangeEventHandler;
       _dge100.HdmiOut.StreamChange += Dge100_StreamChangeEventHandler;
       _dge100.HdmiOut.ConnectedDevice.DeviceInformationChange += Dge100_DeviceInfoChangeEventHandler;

       _dge100.UsbPort.UsbHidChange += Dge100_UsbHidChangeEventHandler;

       //Reserved sig handler initialization
       _dge100.ExtenderAuthenticationReservedSigs.DeviceExtenderSigChange += ExtenderAuthenticationReservedSigs_DeviceExtenderSigChange;

       _dge100.Register();
   }

   // Method to toggle the All Audio Toggle Sig value for DGE-100.
   public void AllAudioToggleCmd(String args)
   {
       if (_dge100.ExtenderAudioReservedSigs.AllAudioOnFeedback.BoolValue)
       {
           CrestronConsole.ConsoleCommandResponse("Disable Audio\r\n");
           _dge100.ExtenderAudioReservedSigs.AllAudioOff();
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Enable Audio\r\n");
           _dge100.ExtenderAudioReservedSigs.AllAudioOn();
       }
   }

   // Sig callback used to read Authentication Control Reserved Sig information received from the DGE-100.
   static void ExtenderAuthenticationReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
   {
       //Sig change for the extender
       if (args.Sig == _dge100.ExtenderAuthenticationReservedSigs.AuthenticationOnFeedback)
       {
           CrestronConsole.PrintLine("AuthenticationOnFeedback: {0}", args.Sig.BoolValue);
       }
   }

   // Method to handle HDMI Out/In sig change events for DGE-100 Device.
   static void Dge100EventHandler(GenericBase device, BaseEventArgs args)
   {
       if (device.ID == 4)
       {
           CrestronConsole.PrintLine("HdmiIn");
       }
       else if (device.ID == 5)
       {
           CrestronConsole.PrintLine("HdmiOut");
       }

       if (args.EventId == DMInputEventIds.SourceSyncEventId)
       {
           CrestronConsole.PrintLine("Sync detected on DGE-100, SyncDetectedFeedback value is {0}", _dge100.HdmiIn.SyncDetectedFeedback.BoolValue);
       }
       else if (args.EventId == DMOutputEventIds.SyncDetectedEventId)
       {
           CrestronConsole.PrintLine("Sync detected on DGE-100, SyncDetectedFeedback value is {0}", _dge100.HdmiOut.SyncDetectedFeedback.BoolValue);
       }
   }

   // Method to handle USB HID sig change events for DGE-100 Device.
   static void Dge100_UsbHidChangeEventHandler(UsbHidPort port, UsbHidEventArgs args)
   {
       if (args.EventId == UsbHidEventIds.DeviceDetectedEventId)
       {
           CrestronConsole.PrintLine("{0} device Device Detected Feedback Event Occurred\r\n", _dge100.UsbPort.ToString());
       }
   }

   // Method to handle HDMI Out Device Info sig change events for DGE-100 Device.
   static void Dge100_DeviceInfoChangeEventHandler(ConnectedDeviceInformation device, ConnectedDeviceEventArgs args)
   {
       if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
       {
           CrestronConsole.PrintLine("{0} device Manufacturer Name Feedback Event Occurred\r\n", _dge100.HdmiOut.ConnectedDevice.ToString());
       }
   }

   // Method to handle HDMI Out/In Stream sig change events for DGE-100 Device.
   static void Dge100_StreamChangeEventHandler(Stream stream, StreamEventArgs args)
   {
       if (args.EventId == DMInputEventIds.AudioFormatEventId)
       {
           CrestronConsole.PrintLine("{0} device Audio Format Feedback Event Occurred\r\n", stream.ToString());
       }
   }

   // Method to handle HDMI Out/In VideoAttribute sig change events for DGE-100 Device.
   static void Dge100_VideoAttributeEventHandler(object sender, GenericEventArgs args)
   {
       if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
       {
           CrestronConsole.PrintLine("{0} device Horizontal Resolution Feedback Event Occurred\r\n", sender.ToString());
       }
   }

   // Method to handle HDMI Out/In CEC sig change events for DGE-100 Device.
   static void Dge100CecChangeEventHandler(Cec device, CecEventArgs args)
   {
       if (args.EventId == CecEventIds.ErrorFeedbackEventId)
       {
           CrestronConsole.PrintLine(string.Format("Device: {0} - Cec Error Detected Status: {1}", _dge100.ToString(), _dge100.HdmiOut.StreamCec.ErrorFeedback.BoolValue));
       }
   }

member.example:
The following will show an Tpmc8XGA loading SmartObjects off the file system. 

//Create a panel and load up SmartObjects
var sgPanel = new Tpmc8XGA(0x05, this);
sgPanel.LoadSmartObjects(Directory.GetApplicationDirectory() + "\\xPanel.sgd");

The following will load SmartObjects from a MemoryStream. 

MemoryStream memStream;
//Populate a memory stream with an sgd off the file system.
using (var filestream = new FileStream(Directory.GetApplicationDirectory() + "\\xPanel.sgd", FileMode.Open, FileAccess.Read))
{
    var fileBytes = new byte[filestream.Length];
    filestream.Read(fileBytes, 0, (int)filestream.Length);
    memStream = new MemoryStream((int)filestream.Length);
    memStream.Write(fileBytes, 0, (int)filestream.Length);
    memStream.Seek(0, SeekOrigin.Begin);
}

//Create a new panel
var sgPanel = new Tpmc8XGA(0x30, this);
//Load SmartObjects from the memory stream.
sgPanel.LoadSmartObjects(memStream);

The following will first show an Xpanel loading SmartObjects off the file system. The second panel shares the same SmartObject information of the first.

//Create a panel and load up SmartObjects
var sgPanel = new Tpmc8XGA(0x05, this);
sgPanel.LoadSmartObjects(Directory.GetApplicationDirectory() + "\\xPanel.sgd");


//Create a second panel and load same SmartObject information.
var secondSgPanel = new XpanelForSmartGraphics(0x06, this); 
secondSgPanel.LoadSmartObjects(sgPanel);

