MPB3101 _mpb3X01 = null;

/// 
/// Constructor of the Control System Class. Make sure the constructor always exists.
/// If it doesn't exit, the code will not run on your 3-Series processor.
/// 
public ControlSystemMPB3X01()
{
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "MPB3-X01 CreateDevice Command", ConsoleAccessLevelEnum.AccessProgrammer);
}

public void CreateDeviceCmd(string args)
{
    try
    {
        _mpb3X01 = new MPB3101(0x11, this);
        _mpb3X01.ButtonStateChange += ControlSystemMpb3X01_ButtonStateChange;
        _mpb3X01.PanelStateChange += ControlSystemMpb3X01_PanelStateChange;
        _mpb3X01.BaseEvent += ControlSystemBaseEventChange;
        _mpb3X01.Register();

        if (_mpb3X01.Registered)
        {
            CrestronConsole.PrintLine("{0} registeration is successfull", _mpb3X01.Name);
        }
    }
    catch (Exception e)
    {
        ErrorLog.Error("Error in the constructor: {0}", e.Message);
    }
}

public void ControlSystemBaseEventChange(GenericBase device, BaseEventArgs args)
{
    CrestronConsole.PrintLine("Event {0} received", args.EventId);
}

void ControlSystemMpb3X01_PanelStateChange(GenericBase device1, BaseEventArgs args)
{
    var device = _mpb3X01;

    if (args.EventId == FrontPanelEventIds.BeepEnabledEventId)
    {
        CrestronConsole.PrintLine("BeepEnabledFeedBack event received, Sig is {0}", device.ButtonPressBeepingEnabledFeedBack);
    }
}

static void ControlSystemMpb3X01_ButtonStateChange(GenericBase device, ButtonEventArgs args)
{
    CrestronConsole.PrintLine("{0} state change event received", args.Button);
}

