   static Dvphd _dvphd;

   public ControlSystemDvphd()
   {
       //Register console commands
       CrestronConsole.AddNewConsoleCommand(BacklightOnCmd, "BackLightOn", "This is a DVPHD BackLightOn command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(BacklightOffCmd, "BackLightOff", "This is a DVPHD BackLightOff command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(AnnotationEffectDelayCmd, "AnnotationEffectDelay", "This is a DVPHD AnnotationEffectDelay command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DVPHD CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(VpgTouchCalibStartCmd, "VpgTouchCalibStart", "This is a DVPHD VpgTouchCalibStart command", ConsoleAccessLevelEnum.AccessOperator);
   }

   //This method is to create the DVPHD device and register with control system
   public void CreateDeviceCmd(String args)
   {
       //Initialize the DVPHD device
       _dvphd = new Dvphd(0x16, this);

       for (uint i = 1; i <= 8; i++)
       {
           _dvphd.ExtenderSourceControlReservedSigs[i].Use();
       }

       _dvphd.ExtenderSmartTechnologiesStatusReservedSigs.Use();
       _dvphd.ExtenderVpgInterfaceReservedSigs.Use();
       _dvphd.ExtenderAnnotation2ReservedSigs.Use();

       //Register the DVPHD with the Control system
       //This will enable the gateway connection ("127.0.0.1") in the system at the 
       //ID specified above that the DVPHD will connect to.
       _dvphd.Register();

       CrestronConsole.PrintLine("{0} is registered successfully", _dvphd.ToString());

       _dvphd.SigChange += DvphdSigChange;

       //Reserved sig handler initialization
       for (uint i = 1; i <= 8; i++)
       {
           _dvphd.ExtenderSourceControlReservedSigs[i].VideoAttributes.AttributeChange +=
               ExtenderSourceControlVideoAttributes_DeviceExtenderSigChange;
           _dvphd.ExtenderSourceControlReservedSigs[i].VideoControls.ControlChange +=
               ExtenderSourceControlVideoControls_DeviceExtenderSigChange;
           _dvphd.ExtenderSourceControlReservedSigs[i].DeviceExtenderSigChange +=
               ExtenderSourceControlReservedSigs_DeviceExtenderSigChange;
       }
       _dvphd.ExtenderSmartTechnologiesStatusReservedSigs.DeviceExtenderSigChange += ExtenderSmartTechnologiesStatusReservedSigs_DeviceExtenderSigChange;
       _dvphd.ExtenderVpgInterfaceReservedSigs.DeviceExtenderSigChange += ExtenderVpgInterfaceReservedSigs_DeviceExtenderSigChange;
       _dvphd.ExtenderAnnotation2ReservedSigs.DeviceExtenderSigChange += ExtenderAnnotation2ReservedSigs_DeviceExtenderSigChange;
   }

   //This method is to trigger the VPG touch calibration
   static void VpgTouchCalibStartCmd(string cmdparameters)
   {
       _dvphd.ExtenderVpgInterfaceReservedSigs.VPGTouchCalibrationStart();
   }

   //This method is to set the delay, in seconds, for the annotation effect to occur after the annotation has been drawn.
   static void AnnotationEffectDelayCmd(string cmdparameters)
   {
       _dvphd.ExtenderAnnotation2ReservedSigs.AnnotationEffectDelay.UShortValue = 50;
   }

   //This method is to set the Backlight On for DVPHD
   public void BacklightOnCmd(String args)
   {
       CrestronConsole.ConsoleCommandResponse("Switch On Backlight\r\n");
       _dvphd.ExtenderSystemReservedSigs.BacklightOn();
   }

   //This method is to set the Backlight Off for DVPHD
   public void BacklightOffCmd(String args)
   {
       CrestronConsole.ConsoleCommandResponse("Switch Off Backlight\r\n");
       _dvphd.ExtenderSystemReservedSigs.BacklightOff();
   }


// Sig callback used to read information received from the _dvphd.
   static void DvphdSigChange(GenericBase currentDevice, SigEventArgs args)
   {
       if (args.Event == eSigEvent.BoolChange)
       {
           var tempBooleanSig = args.Sig as BoolOutputSig;

           if (tempBooleanSig != null)
           {
               if (tempBooleanSig.Number <= 16000)
               {
                   //This means Boolean "sig" changed on the _dvphd
                   //Just print some information for now.
                   CrestronConsole.PrintLine("Boolean Sig {0}'s state {1}", tempBooleanSig.Number, tempBooleanSig.BoolValue);

                   _dvphd.BooleanInput[50].BoolValue = true;
               }
               else
               {
                   CrestronConsole.PrintLine("Boolean Sig {0} is not processed", tempBooleanSig.Number);
               }
           }
       }
       else if (args.Event == eSigEvent.UShortChange)
       {
           var tempIntegerSig = args.Sig as UShortOutputSig;

           if (tempIntegerSig != null)
           {
               if (tempIntegerSig.Number <= 16000)
               {
                   //This means Integer "cue" changed on the _dvphd
                   //Just print some information for now.
                   CrestronConsole.PrintLine("Integer Sig {0}'s state {1}", tempIntegerSig.Number, tempIntegerSig.UShortValue);

                   _dvphd.UShortInput[tempIntegerSig.Number].UShortValue = tempIntegerSig.UShortValue;
               }
               else
               {
                   CrestronConsole.PrintLine("Integer Sig {0} is not processed", tempIntegerSig.Number);
                }
           }
       }
       else if (args.Event == eSigEvent.StringChange)
       {
           var tempStringSig = args.Sig as StringOutputSig;

           if (tempStringSig != null)
           {
               if (tempStringSig.Number <= 16000)
               {
                   //This means String "cue" changed on the _dvphd
                   //Just print some information for now.
                   CrestronConsole.PrintLine("String Sig {0}'s state {1}", tempStringSig.Number, tempStringSig.StringValue);

                   _dvphd.StringInput[tempStringSig.Number].StringValue = tempStringSig.StringValue;
               }
               else
               {
                   CrestronConsole.PrintLine("String Sig {0} is not processed", tempStringSig.Number);
                }
           }
       }
   }

   //This method is to handle the Source Control Reserved Sig Events
   static void ExtenderSourceControlReservedSigs_DeviceExtenderSigChange(DeviceExtender extender, SigEventArgs args)
   {
       CrestronConsole.PrintLine("Source Control Reserved Sig {0}", args.Sig.Number);

       for (uint i = 1; i <= 8; i++)
       {
           //Sig change for the extender
           if (args.Sig == _dvphd.ExtenderSourceControlReservedSigs[i].NoVideoFeedback)
           {
               CrestronConsole.PrintLine("NoVideoFeedback: {0}", args.Sig.BoolValue);
               break;
           }
           if (args.Sig == _dvphd.ExtenderSourceControlReservedSigs[i].NoiseReductionFeedback)
           {
               CrestronConsole.PrintLine("NoiseReductionFeedback: {0}", args.Sig.UShortValue);
               break;
           }
       }
   }

   //This method is to handle the Video Controls Sig events of Source Control Extender
   static void ExtenderSourceControlVideoControls_DeviceExtenderSigChange(object sender, GenericEventArgs args)
   {
       if (args.EventId == VideoControlsEventIds.BlueFeedbackEventId)
       {
           CrestronConsole.Print("BlueFeedback Event received, BlueFeedback sig value is {0}\r\n", ((VideoControlsEnhanced)sender).BlueFeedback.UShortValue);
       }
   }

   //This method is to handle the Video Attributes Sig events of Source Control Extender
   static void ExtenderSourceControlVideoAttributes_DeviceExtenderSigChange(object sender, GenericEventArgs args)
   {
       if (args.EventId == VideoAttributeEventIds.HdcpActiveFeedbackEventId)
       {
           CrestronConsole.Print("HdcpActiveFeedback Event received, and HdcpActiveFeedback sig state is  {0}\r\n", ((DvphdVideoAttributes)sender).HdcpActiveFeedback.BoolValue);
       }
   }

   //This method is to handle the Annotation 2 Reserved Sig Events
