static Tsr310 _tsr310;

// Constructor of the Control System Class. Make sure the constructor always exists.
public ControlSystemTsr310()
{
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "TSR-310 CreateDevice Command", ConsoleAccessLevelEnum.AccessProgrammer);
}

public void Tsr310ButtonStateChange(GenericBase device, ButtonEventArgs args)
{
    CrestronConsole.PrintLine("Button{0} name is {1} state is {2}", args.Button.Number, args.Button.Name, args.Button.State);
}

//Method to create the device
public void CreateDeviceCmd(string args)
{
    try
    {
        _tsr310 = new Tsr310(0x20, this);
        Thread.MaxNumberOfUserThreads = 20;

        _tsr310.ExtenderAuthenticationReservedSigs.Use();
        _tsr310.ExtenderAutoUpdateReservedSigs.Use();
        _tsr310.ExtenderBatteryReservedSigs.Use();
        _tsr310.ExtenderSystem4ReservedSigs.Use();

        _tsr310.ButtonStateChange += Tsr310ButtonStateChange;
        _tsr310.ExtenderAuthenticationReservedSigs.DeviceExtenderSigChange += Authentication_DeviceExtenderEventHandler;
        _tsr310.ExtenderAutoUpdateReservedSigs.DeviceExtenderSigChange += AutoUpdate_DeviceExtenderEventHandler;
        _tsr310.ExtenderBatteryReservedSigs.DeviceExtenderSigChange += Battery_DeviceExtenderEventHandler;
        _tsr310.ExtenderSystem4ReservedSigs.DeviceExtenderSigChange += System4_DeviceExtenderEventHandler;
    }

    catch (Exception e)
    {
        ErrorLog.Error("Error in the constructor: {0}", e.Message);
    }
}

//Event handler for System4 Reserved joins
static void System4_DeviceExtenderEventHandler(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    if (args.Event == eSigEvent.BoolChange)
    {
        if (args.Sig.Number == _tsr310.ExtenderSystem4ReservedSigs.SystemBatteryHealthIndicatorFeedback.Number)
        {
            CrestronConsole.PrintLine("System4 SystemBatteryHealthIndicatorFeedback Sig Change Event for Received, Sig is {0}", args.Sig.BoolValue);
        }
    }
}

//Event handler for Battery Reserved joins
static void Battery_DeviceExtenderEventHandler(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    if (args.Event == eSigEvent.BoolChange)
    {
        if (args.Sig.Number == _tsr310.ExtenderBatteryReservedSigs.DockPowerStatusFeedback.Number)
        {
            CrestronConsole.PrintLine("Battery DockPowerStatusFeedback Sig Change Event for Received, Sig is {0}", args.Sig.BoolValue);
        }
    }
}

//Event handler for Auto Update Reserved joins
static void AutoUpdate_DeviceExtenderEventHandler(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    if (args.Event == eSigEvent.BoolChange)
    {
        if (args.Sig.Number == _tsr310.ExtenderAutoUpdateReservedSigs.EnableFeedback.Number)
        {
            CrestronConsole.PrintLine("Auto Update EnableFeedback Sig Change Event for Received, Sig is {0}", args.Sig.BoolValue);
        }
    }
}

//Event handler for Authentication Reserved joins
static void Authentication_DeviceExtenderEventHandler(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    if (args.Event == eSigEvent.BoolChange)
    {
        if (args.Sig.Number == _tsr310.ExtenderAuthenticationReservedSigs.AuthenticationOnFeedback.Number)
        {
            CrestronConsole.PrintLine("Authentication AuthenticationOnFeedback Sig Change Event for Received, Sig is {0}",
                args.Sig.BoolValue);
        }
    }
}
