example.code:
namespace CrestronConnectedV2Test
{
    public class ControlSystemCrestronConnectedDisplayV2 : CrestronControlSystem
    {
        static CrestronConnectedDisplayV2 _ccdv2;

        public ControlSystemCrestronConnectedDisplayV2()
        {
            //Register console commands
            CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is an Crestron Connected Display V2 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
        }

        public void CreateDeviceCmd(String args)
        {
            _ccdv2 = new CrestronConnectedDisplayV2(0x11, this);

            _ccdv2.BaseEvent += CrestronConnectedDisplayV2BaseEvent;

            if (eDeviceRegistrationUnRegistrationResponse.Success == _ccdv2.Register())
            {
                CrestronConsole.PrintLine("{0} device successfully registered\r\n", _ccdv2.ToString());
            }
        }

        public void CrestronConnectedDisplayV2BaseEvent(GenericBase device, BaseEventArgs args)
        {
            CrestronConsole.PrintLine("BaseEvent type: {0} from device:{1} triggered. Event ID is {3}", args.GetType(), device.ToString(), args.EventId);
        }
    }
}
