example.code:
CnxPad8 _myCnxPad;

public ControlSystemCnxPad()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is an CNX-PAD CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(TestSigsCmd, "TestSigs", "This is an CNX-PAD TestSigs command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(SetSigsCmd, "SetSigs", "This is an CNX-PAD SetSigs command", ConsoleAccessLevelEnum.AccessOperator);
}

public void CreateDeviceCmd(String args)
{
    _myCnxPad = new CnxPad8(0x03, this);

    Thread.MaxNumberOfUserThreads = 20;
    _myCnxPad.Register();
    CrestronConsole.PrintLine("{0} device successfully registered\r\n", _myCnxPad.ToString());
}

public void TestSigsCmd(String args)
{
    int count = 0;

    CrestronConsole.PrintLine("Details of {0}", _myCnxPad.ToString());
    foreach (var room in _myCnxPad.RoomCnxPad)
    {
        count++;
        CrestronConsole.PrintLine("Mute{0}={1}", count, room.MuteOn.BoolValue);
        CrestronConsole.PrintLine("Room-{0}-On={1}", count, room.RoomOn.BoolValue);
        CrestronConsole.PrintLine("Loudness{0}={1}", count, room.EnableLoudness.BoolValue);
        CrestronConsole.PrintLine("Src for rm{0}={1}", count, room.AudioSourceForRoom);
        CrestronConsole.PrintLine("Volume{0}={1}", count, room.Volume.ShortValue);
        CrestronConsole.PrintLine("Balance{0}={1}", count, room.Balance.ShortValue);
        CrestronConsole.PrintLine("Bass{0}={1}", count, room.Bass.ShortValue);
        CrestronConsole.PrintLine("Treble{0}={1}", count, room.Treble.ShortValue);
        CrestronConsole.PrintLine("MaxVol{0}={1}", count, room.MaximumVolume.ShortValue);
        CrestronConsole.PrintLine("MinVol{0}={1}", count, room.MinimumVolume.ShortValue);
    }
    count = 0;
    foreach (var source in _myCnxPad.Source)
    {
        count++;
        CrestronConsole.PrintLine("CompSrc{0}={1}", count, source.Compensation.ShortValue);
    }
    CrestronConsole.PrintLine("Details of {0} successfully printed", _myCnxPad.ToString());
}

public void SetSigsCmd(String args)
{
    short count = 0;

    CrestronConsole.PrintLine("Details of {0}", _myCnxPad.ToString());
    foreach (var room in _myCnxPad.RoomCnxPad)
    {
        count++;
        room.MuteOn.BoolValue = true;
        room.RoomOn.BoolValue = true;
        room.EnableLoudness.BoolValue = true;
        room.AudioSourceForRoom = eAudioSource.RCAInput1;
        room.Volume.ShortValue = (short)(4+count);
        room.Balance.ShortValue = (short)(5+count);
        room.Bass.ShortValue = (short)(6+count);
        room.Treble.ShortValue = (short)(7+count);
        room.MaximumVolume.ShortValue = (short)(8+count);
        room.MinimumVolume.ShortValue = (short)(9+count);
    }
    count = 0;
    foreach (var source in _myCnxPad.Source)
    {
        count++;
        source.Compensation.ShortValue = (short)(10+count);
    }
    CrestronConsole.PrintLine("Details of {0} successfully set", _myCnxPad.ToString());
}

static Dsp1283MatrixMixer _dsp128x;

public ControlSystemDsp128x()
{
    //Register console commands 
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DSP-128X CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the DSP-128X device object
public void CreateDeviceCmd(String args)
{
    _dsp128x = new Dsp1283MatrixMixer(0x16, this);

    if ((_dsp128x.Name == "DSP-1282") || (_dsp128x.Name == "DSP-1283"))
    {
        _dsp128x.ExtenderPOTSReservedSigs.Use();
        _dsp128x.ExtenderVOIPReservedSigs.Use();
    }

    _dsp128x.BaseEvent += Dsp128xBaseEventHandler;
    _dsp128x.DspMixerAudioAttributeEvent += Dsp128xAudioAttributeEventHandler;

    if ((_dsp128x.Name == "DSP-1282") || (_dsp128x.Name == "DSP-1283"))
    {
        _dsp128x.ExtenderPOTSReservedSigs.DeviceExtenderSigChange +=
            ExtenderPOTSReservedSigs_DeviceExtenderSigChange;
        _dsp128x.ExtenderVOIPReservedSigs.DeviceExtenderSigChange +=
            ExtenderVoipReservedSigs_DeviceExtenderSigChange;
    }

    //Register the DSP128x with the Control system
    _dsp128x.Register();
}

// Sig callback used to read VOIP Reserved Sig information received from the DSP128x.
static void ExtenderVoipReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    CrestronConsole.PrintLine("VOIP Reserved Sig {0}", args.Sig.Number);

    //Sig change for the extender
    if (args.Sig == _dsp128x.ExtenderVOIPReservedSigs.ConnectedFeedback)
    {
        CrestronConsole.PrintLine("ConnectedFeedback: {0}", args.Sig.BoolValue);
    }
}

// Sig callback used to read POTS Reserved Sig information received from the DSP128x.
static void ExtenderPOTSReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    CrestronConsole.PrintLine("POTS Reserved Sig {0}", args.Sig.Number);
    if (args.Sig == _dsp128x.ExtenderPOTSReservedSigs.ConnectedFeedback)
    {
        CrestronConsole.PrintLine("ConnectedFeedback: {0}", args.Sig.BoolValue);
    }
}

// Sig callback used to read information received from the DSP128x for System/AEC/Auxiliary Input/Output slot sigs.
static void Dsp128xBaseEventHandler(GenericBase currentDevice, BaseEventArgs args)
{
    if (args.EventId == DspMixerBase.LogicOutFeedbackEventId)
    {
        CrestronConsole.PrintLine("LogicOutFeedback event occurred for {0}", currentDevice);
    }
    else if (args.EventId == Dsp128xAecBase.AecEnableFeedbackEventId)
    {
        CrestronConsole.PrintLine("AecEnable{0} event occurred for {1}", args.Index, currentDevice);
    }
}

// Sig callback used to read information received from the DSP128x for Audio Attribute sigs.
static void Dsp128xAudioAttributeEventHandler(GenericBase device, DspMixerAudioAttributeEventArgs args)
{
    if (args.EventId == DspMixerBase.MuteEnableFeedbackEventId)
    {
        CrestronConsole.PrintLine("MuteEnableFeedback event occurred for Audio Attribute {0} of device {1}, value is {2}", args.DspMixerAudioAttribute, device, args.DspMixerAudioAttribute.MuteFeedback.BoolValue);
    }
}

member.summary:
            Indicates the status of temperature reporting. The output is true for as long as temperature reporting is enabled; 
            when false, this indicates that temperature reporting is disabled.

The  event will trigger with .

example.code:
Amp8075 _amp8075;

//Constructor for ControlSystemAmp8075 class
public ControlSystemAmp8075()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a AMP-8075 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the AMP-8075 device
public void CreateDeviceCmd(String args)
{
    _amp8075 = new Amp8075(0x18, this);

    _amp8075.Register();
    CrestronConsole.PrintLine("{0} is registered successfully", _amp8075.ToString());

    _amp8075.BaseEvent += Amp8xxx_SigChangeEventhandler;
}

//Method to handle the AMP-8xxx sig change events
static void Amp8xxx_SigChangeEventhandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == CommercialAmplifier.TemperatureFormatFeedbackEventId)
    {
        CrestronConsole.PrintLine("Temperature Format Feedback Event occurred for device {0}\r\n", device.ToString());
    }
    else if (args.EventId == CommercialAmplifier.TemperatureReportingFeedbackEventId)
    {
        CrestronConsole.PrintLine("Temperature Reporting Feedback Event occurred for device {0}\r\n", device.ToString());
    }
}

CnAmpx12x60 _myCnAmpx;

public ControlSystemCnAmpx()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is an CN-AMPX CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(TestSigsCmd, "TestSigs", "This is an CN-AMPX TestSigs command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(SetSigsCmd, "SetSigs", "This is an CN-AMPX SetSigs command", ConsoleAccessLevelEnum.AccessOperator);
}

public void CreateDeviceCmd(String args)
{
    _myCnAmpx = new CnAmpx12x60(0x08, this);

    Thread.MaxNumberOfUserThreads = 20;
    _myCnAmpx.w += new BaseEventHandler(_myCnAmpx_BaseEvent);
    _myCnAmpx.Register();
    CrestronConsole.PrintLine("{0} device successfully registered\r\n", _myCnAmpx.ToString());
}

public void TestSigsCmd(String args)
{
    CrestronConsole.PrintLine("Details of {0}", _myCnAmpx.ToString());

    CrestronConsole.PrintLine("Main power {0} Sig number is {1}", _myCnAmpx.MainPower.BoolValue, _myCnAmpx.MainPower.Number);
    CrestronConsole.PrintLine("Enable temperature report {0} Sig number is {1}", _myCnAmpx.TemperatureReportEnabled.BoolValue, _myCnAmpx.TemperatureReportEnabled.Number);
    CrestronConsole.PrintLine("Temperature format {0} Sig number is {1}", _myCnAmpx.TemperatureFormat.BoolValue, _myCnAmpx.TemperatureFormat.Number);

    CrestronConsole.PrintLine("Overide feedback {0} Sig number is {1}", _myCnAmpx.OverrideFeedback.BoolValue, _myCnAmpx.OverrideFeedback.Number);
    CrestronConsole.PrintLine("Temperature x10 {0} Sig number is {1}", _myCnAmpx.TemperatureX10Feedback.BoolValue, _myCnAmpx.TemperatureX10Feedback.Number);

    CrestronConsole.PrintLine("Details of {0} successfully printed", _myCnAmpx.ToString());
}

public void SetSigsCmd(String args)
{

    CrestronConsole.PrintLine("Details of {0}", _myCnAmpx.ToString());

    _myCnAmpx.MainPower.BoolValue = true;
    _myCnAmpx.TemperatureReportEnabled.BoolValue = true;
    _myCnAmpx.TemperatureFormat.BoolValue = true;

    CrestronConsole.PrintLine("Details of {0} successfully set", _myCnAmpx.ToString());
}

void _myCnAmpx_BaseEvent(GenericBase device, BaseEventArgs args)
{
    CrestronConsole.PrintLine("CnAmpx BaseEvent triggered");

    if (args.EventId == CnAmpxEventIds.OverrideFeedbackEventId)
    {
        CrestronConsole.PrintLine("OverrideFeedback event for {0} occurred, value is {1}\r\n", device, _myCnAmpx.OverrideFeedback.BoolValue);
    }
    else if (args.EventId == CnAmpxEventIds.TemperatureX10FeedbackEventId)
    {
        CrestronConsole.PrintLine("TemperatureX10Feedback event for {0} occurred value is {1}\r\n", device, _myCnAmpx.TemperatureX10Feedback.UShortValue);
    }
}
