example.code:
static CcsUcCodec300 _ccsUcCodecDevice;

public ControlSystemCcsUcCodec300()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a CcsUcCodec300 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

public void CreateDeviceCmd(String args)
{
    //Initialize the Rigel device for Ethernet Communication.
    _ccsUcCodecDevice = new CcsUcCodec300(0x18, this);

    _ccsUcCodecDevice.ExtenderLyncReservedSigs.Use();

    _ccsUcCodecDevice.BaseEvent += RigelEventHandler;


    //Reserved sig handler initialization
    _ccsUcCodecDevice.ExtenderLyncReservedSigs.DeviceExtenderSigChange += ExtenderLyncMeetingRoomReservedSigs_DeviceExtenderSigChange;

    _ccsUcCodecDevice.Register();
}


// Sig callback used to read Lync Meeting Room Reserved Sig information received from the CcsUcCodec300.
static void ExtenderLyncMeetingRoomReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    //Sig change for the extender
    if (args.Sig == _mercury.ExtenderLyncReservedSigs.ShowProjectFeedback)
    {
        CrestronConsole.PrintLine("ShowProjectFeedback: {0}", args.Sig.BoolValue);
    }
}

