example.code:
static UcEngine _UcEngine;

public ControlSystemUcEngine()
{
    //Register console commands 
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a UCEngine CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

// Creates UC-Engine device object.
public void CreateDeviceCmd(String args)
{
    //Initialize the UcEngine device
    _UcEngine = new UcEngine(0x16, this);

    _UcEngine.ExtenderAudioReservedSigs.Use();
    _UcEngine.ExtenderEthernetReservedSigs.Use();

    _UcEngine.SigChange += UcEngineSigChange;

    //Reserved sig handler initialization
    _UcEngine.ExtenderAudioReservedSigs.DeviceExtenderSigChange += ExtenderAudioReservedSigs_DeviceExtenderSigChange;
    _UcEngine.ExtenderEthernetReservedSigs.DeviceExtenderSigChange += ExtenderEthernetReservedSigs_DeviceExtenderSigChange;

    //Register the UcEngine with the Control system
    //This will enable the gateway connection ("127.0.0.1") in the system at the 
    //ID specified above that the UcEngine will connect to.
    _UcEngine.Register();

    CrestronConsole.PrintLine("{0} device registered successfully", _UcEngine);
}

// Sig callback used to read Audio Reserved Sig information received from the UcEngine.
static void ExtenderAudioReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    CrestronConsole.PrintLine("Audio Reserved Sig {0}", args.Sig.Number);

    switch (args.Sig.Type)
    {
        case eSigType.Bool:

            //Sig change for the extender
            if (args.Sig == _UcEngine.ExtenderAudioReservedSigs.ConfMicMuteOffFeedback)
            {
                CrestronConsole.PrintLine("ConfMicMuteOffFeedback: {0}", args.Sig.BoolValue);
            }
            break;
    }
}

// Sig callback used to read Ethernet Reserved Sig information received from the UcEngine.
static void ExtenderEthernetReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    CrestronConsole.PrintLine("Ethernet Reserved Sig {0}", args.Sig.Number);

    switch (args.Sig.Type)
    {
        case eSigType.String:
            if (args.Sig == _UcEngine.ExtenderEthernetReservedSigs.IpAddressFeedback)
            {
                CrestronConsole.PrintLine("IpAddressFeedback: {0}", args.Sig.StringValue);
            }
            break;
    }
}

// Sig callback used to read information received from the _UcEngine.
static void UcEngineSigChange(GenericBase currentDevice, SigEventArgs args)
{
    if (args.Event == eSigEvent.BoolChange)
    {
        var tempBooleanSig = args.Sig as BoolOutputSig;

        if (tempBooleanSig != null)
        {
            if (tempBooleanSig.Number <= 16000)
            {
                //This means Boolean "cue" changed on the _UcEngine
                CrestronConsole.PrintLine("Boolean Sig {0}'s state {1}", tempBooleanSig.Number, tempBooleanSig.BoolValue);
            }
            else
            {
                CrestronConsole.PrintLine("Boolean Sig {0} is not processed", tempBooleanSig.Number);
            }
        }
    }
    else if (args.Event == eSigEvent.UShortChange)
    {
        var tempIntegerSig = args.Sig as UShortOutputSig;

        if (tempIntegerSig != null)
        {
            if (tempIntegerSig.Number <= 16000)
            {
                //This means Integer "cue" changed on the _UcEngine
                CrestronConsole.PrintLine("Integer Sig {0}'s state {1}", tempIntegerSig.Number, tempIntegerSig.UShortValue);
            }
            else
            {
                CrestronConsole.PrintLine("Integer Sig {0} is not processed", tempIntegerSig.Number);
            }
        }
    }
    else if (args.Event == eSigEvent.StringChange)
    {
        var tempStringSig = args.Sig as StringOutputSig;

        if (tempStringSig != null)
        {
            if (tempStringSig.Number <= 16000)
            {
                //This means String "cue" changed on the _UcEngine
                CrestronConsole.PrintLine("String Sig {0}'s state {1}", tempStringSig.Number, tempStringSig.StringValue);
            }
            else
            {
                CrestronConsole.PrintLine("String Sig {0} is not processed", tempStringSig.Number);
            }
        }
    }   
}
