example.code:
static DmNvx351 myNvx351;

public ControlSystemDmNvx351()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DM-NVX-351 CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

// Method to handle top level sig change events for DM-NVX-351 Device.
static void DmNvx351EventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == DMInputEventIds.StartEventId)
    {
        CrestronConsole.PrintLine("Start event occurred on DM-NVX-351, StartFeedback value is {0}", myNvx351.Control.StartFeedback.BoolValue);
    }
}

//Event handler for Stream sig changes
static void DmNvx351_StreamChangeEventHandler(Stream stream, StreamEventArgs args)
{
    //Stream Transmit
    if (args.EventId == DMOutputEventIds.StatisticsDisableEventId)
    {
        CrestronConsole.PrintLine("{0} device Statistics Disabled Event Occurred\r\n", stream.ToString());
    }

    //HDMI In
    if (args.EventId == DMInputEventIds.SourceSyncEventId)
    {
        CrestronConsole.PrintLine("{0} device Sync Detected Feedback Event Occurred\r\n", stream.ToString());
    }

    //HDMI Out
    else if (args.EventId == DMOutputEventIds.HotplugDetectedEventId)
    {
        CrestronConsole.PrintLine("{0} device Hot Plug Detected Feedback Event Occurred\r\n", stream.ToString());
    }
}

//Event handler for Video Attribute sig changes
static void DmNvx351VideoAttributeEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} device Horizontal Resolution Feedback Event Occurred\r\n", sender.ToString());
    }
}

//Event handler for Cec sig changes
static void DmNvx351CecChangeEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine(string.Format("Device: {0} - Cec Error Detected Status: {1}", myNvx351.ToString(), myNvx351.HdmiOut.StreamCec.ErrorFeedback.BoolValue));
    }
}

//Event handler for Device Info sig changes
static void DmNvx351DeviceInfoChangeEventHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("{0} device Manufacturer Name Feedback Event Occurred\r\n", myNvx351.HdmiOut.ConnectedDevice.ToString());
    }
}

//Method to handle Text/Image Overlay Properties sig change events for DM-NVX-351 Device.
static void DmNvx351OverlayPropertiesEventHandler(object device, OverlayPropertiesEventArgs args)
{
    if (args.EventId == OverlayEventIds.EnabledEventId)
    {
        CrestronConsole.PrintLine("Enabled Event Received for {0}\r\n", args.OverlayProperties.Type);
    }
}

//Method to create device
public void CreateDeviceCmd(String args)
{
    //Initialize the DM-NVX-351 device for Ethernet Communication.
    myNvx351 = new DmNvx351(0x18, this);

    myNvx351.BaseEvent += DmNvx351EventHandler;

    for (uint i = 1; i <= 2; i++)
    {
        myNvx351.HdmiIn[i].VideoAttributes.AttributeChange += DmNvx351VideoAttributeEventHandler;
        myNvx351.HdmiIn[i].StreamCec.CecChange += DmNvx351CecChangeEventHandler;
        myNvx351.HdmiIn[i].StreamChange += DmNvx351_StreamChangeEventHandler;
    }

    myNvx351.HdmiOut.VideoAttributes.AttributeChange += DmNvx351VideoAttributeEventHandler;
    myNvx351.HdmiOut.StreamCec.CecChange += DmNvx351CecChangeEventHandler;
    myNvx351.HdmiOut.StreamChange += DmNvx351_StreamChangeEventHandler;
    myNvx351.HdmiOut.ConnectedDevice.DeviceInformationChange += DmNvx351DeviceInfoChangeEventHandler;

    myNvx351.SourceReceive.VideoAttributes.AttributeChange += DmNvx351VideoAttributeEventHandler;
    myNvx351.SourceReceive.StreamChange += DmNvx351_StreamChangeEventHandler;

    myNvx351.SourceTransmit.VideoAttributes.AttributeChange += DmNvx351VideoAttributeEventHandler;
    myNvx351.SourceTransmit.StreamChange += DmNvx351_StreamChangeEventHandler;

    myNvx351.TextProperties.OverlayPropertiesChange += DmNvx351OverlayPropertiesEventHandler;

    myNvx351.Register();
}

