    _dmXx4kSfp.BaseEvent += DmfXx4kSfpEventHandler;

    _dmXx4kSfp.HdmiIn[1].StreamChange += DmfXx4kSfpStreamChangeEventHandler;
    _dmXx4kSfp.HdmiIn[2].StreamChange += DmfXx4kSfpStreamChangeEventHandler;
    _dmXx4kSfp.HdmiOut.StreamChange += DmfXx4kSfpStreamChangeEventHandler;
    _dmXx4kSfp.TextProperties.OverlayPropertiesChange += DmfXx4kSfpTextOverlayEventHandler;
    _dmXx4kSfp.RemoteEnd.HdmiIn.StreamChange += DmfXx4kSfpStreamChangeEventHandler;
    _dmXx4kSfp.RemoteEnd.HdmiOut.StreamChange += DmfXx4kSfpStreamChangeEventHandler;
    _dmXx4kSfp.RemoteEnd.TextProperties.OverlayPropertiesChange += DmfXx4kSfpTextOverlayEventHandler;

    _dmXx4kSfp.Register();
}

