    if (args.EventId == DMInputEventIds.SourceSyncEventId)
    {
        CrestronConsole.PrintLine("{0} device Sync Detected Feedback Event Occurred\r\n", stream.ToString());
    }

    //HDMI Out
    if (args.EventId == DMOutputEventIds.HotplugDetectedEventId)
    {
        CrestronConsole.PrintLine("{0} device Hot Plug Detected Feedback Event Occurred\r\n", stream.ToString());
    }
}

static DmTx4k302C _DmTx4k302C;

// Method to create control system object this device is connected to.
public ControlSystemDmTx4k302C()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DM-TX-4K-302-C CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

// Method to handle to create DM-TX-4K-302-C Device
public void CreateDeviceCmd(String args)
{
    _DmTx4k302C = new DmTx4k302C(0x17, this);

    _DmTx4k302C.BaseEvent += DmTx4k302CEventHandler;
    _DmTx4k302C.DmOutput.OutputStreamChange += DmOutputStreamChange;
    _DmTx4k302C.HdmiOutput.StreamCec.CecChange += DmTx4k302CHdmiOutCecChangeEventHandler;
    _DmTx4k302C.DmOutput.ConnectedDevice.DeviceInformationChange += ConnectedDeviceOnDeviceInformationChange;

    _DmTx4k302C.VgaInput.InputStreamChange += DmTx4k302CVgaStreamChange;

    for (uint i = 1; i <= 2; i++)
    {
        _DmTx4k302C.HdmiInputs[i].InputStreamChange += HdmiInputOnInputStreamChange;
     }

    _DmTx4k302C.Register();
}

// Method to handle top level sig change events for DM-TX-4K-302-C Device.
static void DmTx4k302CEventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == DmTx4kX02CBase.VideoSourceFeedbackEventId)
    {
        CrestronConsole.PrintLine("Video Source Feedback Event occurred on {0}", _DmTx4k302C.ToString());
    }
}

// Method to handle top level DM Out sig change events for DM-TX-4K-302-C Device.
static void DmOutputStreamChange(EndpointOutputStream outputStream, EndpointOutputStreamEventArgs args)
{
    if (args.EventId == EndpointOutputStreamEventIds.AudioFormatFeedbackEventId)
    {
        CrestronConsole.PrintLine("{Audio Format Feedback Event Received for {0}\r\n", _DmTx4k302C.ToString());
    }
}

// Method to handle top level HDMI Out sig change events for DM-TX-4K-302-C Device.
static void DmTx4k302CHdmiOutCecChangeEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("{Cec Message Received Event Received for {0}\r\n", _DmTx4k302C.ToString());
    }
}

// Method to handle top level Connected Device Information sig change events for DM-TX-4K-302-C Device.
static void ConnectedDeviceOnDeviceInformationChange(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer Event occurred on {0}", _DmTx4k302C.ToString());
    }
}

// Method to handle HDMI Input sig change events for DM-TX-4K-302-C Device.
static void HdmiInputOnInputStreamChange(EndpointInputStream inputStream, EndpointInputStreamEventArgs args)
{
    if (args.EventId == EndpointInputStreamEventIds.AudioFormatFeedbackEventId)
    {
        CrestronConsole.PrintLine("Audio Format Feedback Event Received for {0}\r\n", _DmTx4k302C.ToString());
    }
}

// Method to handle VGA sig change events for DM-TX-4K-302-C Device.
static void DmTx4k302CVgaStreamChange(EndpointInputStream inputStream, EndpointInputStreamEventArgs args)
{
    if (args.EventId == EndpointInputStreamEventIds.AudioFormatFeedbackEventId)
    {
        CrestronConsole.PrintLine("Audio Format Feedback Event ID is received for {0}", inputStream);
    }
}

static Dmc4kHdoSingle _dmc4kHdo;
static Dmc4kHdoSingle _dmc4kHdo1;
sw1 = new DmMd8x8(0x18, this);

public ControlSystemDmc4kHdo()
{
    //Register console commands 
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DMC-4K-HDO CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the DMC-4K-HDO card device
public void CreateDeviceCmd(String args)
{
    sw1 = new DmMd8x8(0x18, this);

    //Initialize the DMC-4K-HDO device
    _dmc4kHdo = new Dmc4kHdoSingle(1, sw1);
    _dmc4kHdo1 = new Dmc4kHdoSingle(4, sw1);

    //Register the DM-MD8x8 with the Control system
    sw1.Register();

    for (uint i = 1; i <= 2; i++)
    {
        object obj = i == 1 ? _dmc4kHdo.Card1 : _dmc4kHdo.Card2;

        ((Dmc4kHdo)obj).HdmiOutput.ConnectedDevice.DeviceInformationChange += ConnectedDeviceSigChangeHandler;
    }

    sw1.DMOutputChange += DMOutputSigChangeEventHandler;

    for (uint j = 1; j <= 8; j++)
    {
        if ((sw1.Outputs[j]) != null)
        {
            CrestronConsole.ConsoleCommandResponse("{0} is initialized\r\n", sw1.Outputs[j], j);
        }
    }
}

//Method to handle the switcher DM Output events
static void DMOutputSigChangeEventHandler(Switch device, DMOutputEventArgs args)
{
   if (args.EventId == DMOutputEventIds.OnlineFeedbackEventId)
   {
        CrestronConsole.PrintLine("Online Feedback event occurred for stream {0}\r\n", device.Outputs[args.Number]);
        CrestronConsole.PrintLine("Online Feedback Value is {0}\r\n", ((Dmc4kHdo)sw1.Outputs[args.Number].Card).OnlineFeedback);
   }       
   else if (args.EventId == DMOutputEventIds.OverscanModeFeedbackEventId)
   {
        CrestronConsole.PrintLine("Overscan Mode Feedback event occurred for {0} device\r\n", device.ToString());
        CrestronConsole.PrintLine("Overscan Mode Feedback Value is {0}\r\n", ((Dmc4kHdo)sw1.Outputs[args.Number].Card).OverscanModeFeedback);
   }
}

//Method to handle the Connected Device sig change events
static void ConnectedDeviceSigChangeHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer event occurred for {0} device of {1}\r\n", connectedDevice.ToString(), connectedDevice.Owner);
        CrestronConsole.PrintLine("Manufacturer Feedback Value is {0}\r\n", connectedDevice.Manufacturer.UShortValue);
    }
}

