static Dmc4kzCDsp _dmc4kzC;
static DmMd8x8 dmSwitcher1;

//ControlSystemDmc4kzC class constructor 
public ControlSystemDmc4kzC()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceAsSwitcherDmInputCmd, "CreateDevSwDmInput", "This is a DMC-4KZ-C CreateDevSwDmInput command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the device as a DM Input Card to a DM Switcher
public void CreateDeviceAsSwitcherDmInputCmd(String args)
{
    //For Switcher
    dmSwitcher1 = new DmMd8x8(0x20, this);
    _dmc4kzC = new Dmc4kzCDsp(1, dmSwitcher1);

    _dmc4kzC.DmInput.VideoAttributes.AttributeChange += DmInput_VideoAttributesEventHandler;
    _dmc4kzC.HdmiOutput.StreamCec.CecChange += DmRmc4Kz_HdmiStreamCecEventHandler;
    dmSwitcher1.DMInputChange += DmSwitcherDmInputEventHandler;
    _dmc4kzC.HdmiOutput.ConnectedDevice.DeviceInformationChange += DmRmc4Kz_HdmiDeviceInfoEventHandler;
    _dmc4kzC.DmInput.StreamCec.CecChange += DmRmc4Kz_DmStreamCecEventHandler;

    //Register the DM Switcher with the Control System
    dmSwitcher1.Register();
}

//Method to handle the DM Input Sig Change Events
public void DmSwitcherDmInputEventHandler(Switch device, DMInputEventArgs args)
{
    if (args.EventId == DMInputEventIds.DacVolumeEventId)
    {
        CrestronConsole.PrintLine("{0} device DAC Volume Event occurred, Stream {1}\r\n", device.ToString(), args.Stream);
    }
}

//Method to handle the HDMI Output Device Information Change Events
public void DmRmc4Kz_HdmiDeviceInfoEventHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer event occurred for {0} device of {1}\r\n", connectedDevice.ToString(), connectedDevice.Owner);
        CrestronConsole.PrintLine("Manufacturer Feedback Value is {0}\r\n", connectedDevice.Manufacturer.UShortValue);
    }
}

//Method to handle the DM Input Video Attribute Change Events
public void DmInput_VideoAttributesEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("Horizontal Resolution Feedback event occurred for {0} device of {1}\r\n", sender.ToString(), ((VideoAttributesBasic)sender).Owner);
    }
}

//Method to handle the HDMI Output Stream CEC Change Events
