{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", _dmc4kzC.HdmiOutput.StreamCec.ToString());
    }
}

