        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", cecDevice.ToString());
    }
}

 /// Switcher.
private static DmMd32x32 _switcher;

// Cards
private static Dmc4kzCDsp _dmc4kzDsp;
private static Dmc4kzCoHdSingle _dmc4kzCoHD;
private static Dmc4kCoHdSingle _dmc4kCoHD;
private static Dmc4kzC _dmc4kzHD;

// Receivers
private static DmRmc4kScalerC _dmRmc4kScalar;

// Transmitters
private static DmTx4k302C _tx4k302;
/// Initialization function which sets up the DM switcher object and its base attributes.
public static bool InitializeDevice(CrestronControlSystem controlSystem)
{
    bool returnValue = false;
    try
    {
        // Initialize our DmMd32x32 object with the cp_id and CrestronControlSystem object.
        _switcher = new DmMd32x32(0x04, controlSystem);

        _switcher.Description = "DM switcher 32 x 23";

        // Event handlers for the switcher.
        _switcher.DMInputChange += new DMInputEventHandler(_switcher_DMInputChange);

        _switcher.DMOutputChange += new DMOutputEventHandler(_switcher_DMOutputChange);

        _switcher.DMSystemChange += new DMSystemEventHandler(_switcher_DMSystemChange);

        _switcher.OnlineStatusChange += new OnlineStatusChangeEventHandler(_switcher_OnlineStatusChange);

        // Now it is time to set up our DM I/O cards There is an order in which initialization needs to occur.
        // 1) Instantiate card objects and with their I/O slot number.
        // 2) Set up routes for input cards to their respected output cards.
        // 3) Instantiate Rx/Tx objects with the I/O cards they are physically connected too.
        CardSetup();

        RecieverSetup();

        TransmitterSetup();

        InitializeRoutes();

        // Applies the systems ID if the device is flagged for private network mode.
        // ask ken about this call.
        _switcher.ApplySystemId();

        // Flag our I/O signals as online.
        _switcher.VideoEnter.BoolValue = true;

        _switcher.AudioEnter.BoolValue = true;

        _switcher.USBEnter.BoolValue = true;

        // Register the switcher 
        // Internally, the switcher will register all cards that are physically connected to it.
        // There is no need to manually registered all defined cards.
        eDeviceRegistrationUnRegistrationResponse resp = _switcher.Register();

        // Check if the switcher has been registered.
        if (resp != eDeviceRegistrationUnRegistrationResponse.Success)
        {
            ErrorLog.Error("Unable to register DM switcher: {0}", resp.ToString());
        }
        else
        {
            ErrorLog.Notice("Dm switcher registered. {0}", resp.ToString());
        }
            returnValue = true;
        }
    catch (Exception e)
    {
        ErrorLog.Error("Error while initializing DM switcher: {0}", e.ToString());
    }

    return returnValue;
}


/// Sets up our cards that are within the DM switcher. 
/// Currently using a DMC-4kz-C-DSP, DMC-4k-C, and Dmc-4k-CO-HD.
private static void CardSetup()
{
    try
    {
        // ADD COMMENTS ABOUT SLOT NUMBER RELATION.

        // Initialize card objects, each are assigned a slot number and given the Dm Switcher it is attached to.

        //IMPORTANT: In this example we are letting the DM switcher class handle all card registration. Do not register each card.
        // Input cards follow numbering on the back panel of the device.
        _dmc4kzDsp = new Dmc4kzCDsp(8, _switcher);
        _dmc4kzHD = new Dmc4kzC(23, _switcher); 

        // Output cards given the slot number they are connected to the DM switcher in and the DM switcher object.
        // Output cards have a different numbering than input cards. They are in in units of two. 
        // So instead of 32 slots numbering it is a 16 slot numbering system where each physical card is two device cards.
        // For more information check the specs for your specific DM switcher device.
        _dmc4kzCoHD = new Dmc4kzCoHdSingle(13, _switcher);
        _dmc4kCoHD = new Dmc4kCoHdSingle(12, _switcher);

    } 
    catch(Exception e)
    {
        ErrorLog.Error("Error while initializing cards: {0}", e.Message);
    }
}

/// Sets up the routing between input cards and output cards. These routes are hard coded as an example. 
/// Change to whatever specific routing configuration needed.
/// NOTE: Video can also be routed in the same way. However, for this example audio is only covered.
private static void InitializeRoutes()
{
    try
    {
        // Assign our audio output to the audio input on card.
        // Will trigger the Input change event on the switcher.
        _switcher.Outputs["outputSlot"].AudioOut = _switcher.Inputs["inputSlot"];
        _switcher.Outputs["outputSlot"].VideoOut = _switcher.Inputs["inputSlot"];


        // Assign our audio / video output audio input on card.
        // Will trigger the Input change event on the switcher.
        _switcher.Outputs["outputSlot"].AudioOut = _switcher.Inputs["inputSlot"];
        _switcher.Outputs["outputSlot"].VideoOut = _switcher.Inputs["inputSlot"];
    }
    catch (Exception e)
    {
        ErrorLog.Notice("Error while initializing input / output routing: {0}", e.Message);
    }
}


/// Set up for receivers connected to the DM Output cards. 
/// Receivers are output endpoints.
private static void RecieverSetup()
{
    try
    {
        // Initialize the object with a IPID and the output card physically connected to the reciever.  
        _dmRmc4kScalar = new DmRmc4kScalerC(0x06, _switcher.Outputs[24]);

        _dmRmc4kScalar.Description = "Rmc 4k scalar output 24";

        // sets up supporting functionality for transmitting serial data over comm port 1.
        RecieverSerialSetup();
    }
    catch (Exception e)
    {
        ErrorLog.Error("Error while initalizing recievers: {0}", e.Message);
    }
}

