//Method to create the device
public void CreateDeviceCmd(String args)
{
    _dmRmc4kz100C = new DmRmc4kz100C(0x18, this);

    //Register the device with the Control System
    _dmRmc4kz100C.Register();

    _dmRmc4kz100C.DmInputStreamCec.CecChange += DmInput_StreamCecEventHandler;
    _dmRmc4kz100C.StreamCec.CecChange += DmRmc4Kz_HdmiStreamCecEventHandler;
}

//Method to handle the HDMI Out Stream CEC Events
public void DmRmc4Kz_HdmiStreamCecEventHandler(Cec cecdevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", _dmRmc4kz100C.StreamCec.ToString());
    }
    else if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("{0} Message Received Event Occurred\r\n", _dmRmc4kz100C.StreamCec.ToString());
    }
}

//Method to handle the DM In Stream CEC Events
static void DmInput_StreamCecEventHandler(Cec cecdevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", _dmRmc4kz100C.DmInputStreamCec.ToString());
    }
    else if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("{0} Message Received Event Occurred\r\n", _dmRmc4kz100C.DmInputStreamCec.ToString());
    }
}

// Initialize our DmRmc4kScalarC object, with the output that will be associated with the device on the switcher device.
private static void ReceiverSetup(Switch switcher)
{
    try
   {
       // Initialize the object with a IPID and the output card physically connected to the receiver.  
       _dmRmc4kScalar = new DmRmc4kScalerC(0x06, switcher.Outputs[24]);

       _dmRmc4kScalar.Description = "Rmc 4k scalar output 24";

       // sets up supporting functionality for transmitting serial data over comm port 1.
       ReceiverSerialSetup();
   }
   catch (Exception e)
   {
       ErrorLog.Error("Error while initializing receivers: {0}", e.Message);
   }
}

/// Helper function for setting up sending serial data over comm port 1.
private static void ReceiverSerialSetup()
{
    // event handler for serial data coming through comm
    _dmRmc4kScalar.ComPorts[1].SerialDataReceived += new ComPortDataReceivedEvent(DMSwitchExample_SerialDataReceived);

    if (_dmRmc4kScalar.ComPorts[1].Register() != eDeviceRegistrationUnRegistrationResponse.Success)
    {
        ErrorLog.Error("Error Registering ComPort: {1}", _dmRmc4kScalar.ComPorts[1].DeviceRegistrationFailureReason);
    }
    else
    {
        // Set up the comm port to send serial data.
        _dmRmc4kScalar.ComPorts[1].SetComPortSpec(ComPort.eComBaudRates.ComspecBaudRate19200,
            ComPort.eComDataBits.ComspecDataBits8,
            ComPort.eComParityType.ComspecParityNone,
            ComPort.eComStopBits.ComspecStopBits1,
            ComPort.eComProtocolType.ComspecProtocolRS232,
            ComPort.eComHardwareHandshakeType.ComspecHardwareHandshakeNone,
            ComPort.eComSoftwareHandshakeType.ComspecSoftwareHandshakeNone,
            false
       );
    }
}

/// Sends Serial Data over the Comm 1 port , Event handler for serial data on the port should trigger.
public static void TestSendSerialData(string args)
{
    try
    {
        // Specify our comm port.
        var comm = _dmRmc4kScalar.ComPorts[1];

        // Only send data over the port if it is available.
        if (comm.AvailableForUse)
        {
            // Print out the baud rate on the specific port we are send data over.
            CrestronConsole.PrintLine("Baud rate: {0}", comm.BaudRate.ToString());

            /// Create and start a thread that will send three serial commands to the comm port.
            Thread thrd = new Thread(sendSerialData, comm, Thread.eThreadStartOptions.Running);
        }
    }
    catch (Exception e)
    {
        ErrorLog.Error("Error while testing transmitting serial data: {0}", e.Message);
    }
}

/// Helper function to for sending serial data.
/// Always returns null.
private static object sendSerialData(object obj)
{
    ComPort comm = (ComPort)obj;

    comm.Send("Power\n");
    Thread.Sleep(200);
    comm.Send("DN_Arrow\n");
    Thread.Sleep(200);
    comm.Send("Up_Arrow\n");

    return null;
}

