ConnectedDeviceDeviceInformationChange;
foreach (var input in dmSwitch.VgaInputs)
{
    input.VgaInputPort.VideoControls.ControlChange += VideoControlsControlChange;
}
dmSwitch.DMInputChange += DmSwitchDMInputChange;
dmSwitch.DMSystemChange += DmSwitchDMSystemChange;
dmSwitch.MicrophoneChange += DmSwitchMicrophoneChange;

