public ControlSystemDmc4kzHdXXX()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceAsSwitcherDmInputCmd, "CreateDevSwDmInput", "This is a DMC-4KZ-HD-XXX CreateDevSwDmInput command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the device as DM Switcher Input
public void CreateDeviceAsSwitcherDmInputCmd(String args)
{
    //For Switcher
    _dmSwitcher = new DmMd8x8(0x51, this);
    _dmc4kzHd = new Dmc4kzHdDsp(1, _dmSwitcher);

    _dmc4kzHd.HdmiInput.VideoAttributes.AttributeChange += HdmiInput_VideoAttributesEventHandler;
    _dmc4kzHd.HdmiOutput.StreamCec.CecChange += DmRmc4Kz_HdmiOutputStreamCecEventHandler;
    _dmSwitcher.DMInputChange += DmSwitcherDmInputEventHandler;
    _dmc4kzHd.HdmiOutput.ConnectedDevice.DeviceInformationChange += DmRmc4Kz_HdmiOutputDeviceInfoEventHandler;
    _dmc4kzHd.HdmiInput.StreamCec.CecChange += DmRmc4Kz_HdmiInputStreamCecEventHandler;
    _dmc4kzHd.UsbPort.UsbHidChange += DmRmc4Kz_UsbHidEventHandler;

    //Register the DM Switcher with the Control System
    _dmSwitcher.Register();
}

//Method to handle the USB HID Sig Change Events
static void DmRmc4Kz_UsbHidEventHandler(UsbHidPort usbHidPort, UsbHidEventArgs args)
{
    if (args.EventId == UsbHidEventIds.DeviceDetectedEventId)
    {
        CrestronConsole.PrintLine("Device Detected event occurred for {0} device\r\n", usbHidPort.ToString());
    }
}

//Method to handle the DM Input Sig Change Events
public void DmSwitcherDmInputEventHandler(Switch device, DMInputEventArgs args)
{
    if (args.EventId == DMInputEventIds.DacVolumeEventId)
    {
        CrestronConsole.PrintLine("{0} device DAC Volume Event occurred, Stream {1}\r\n", device.ToString(), args.Stream);
    }
    if (args.EventId == DMInputEventIds.OnlineFeedbackEventId)
    {
        CrestronConsole.PrintLine("Online Feedback event occurred for {0}, OnlineFeedback status is {1}", device.ToString(), _dmc4kzHd.OnlineFeedback.BoolValue);
    }
    if (args.EventId == DMInputEventIds.PresentFeedbackEventId)
    {
        CrestronConsole.PrintLine("Present Feedback event occurred for {0}, PresentFeedback status is {1}", device.ToString(), _dmc4kzHd.PresentFeedback.BoolValue);
    }
}

//Method to handle the HDMI Output Device Information Change Events
public void DmRmc4Kz_HdmiOutputDeviceInfoEventHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer event occurred for {0} device of {1}\r\n", connectedDevice.ToString(), connectedDevice.Owner);
        CrestronConsole.PrintLine("Manufacturer Feedback Value is {0}\r\n", connectedDevice.Manufacturer.UShortValue);
    }
}

//Method to handle the HDMI Input Stream CEC Change Events
public void HdmiInput_VideoAttributesEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("Horizontal Resolution Feedback event occurred for {0} device of {1}\r\n", sender.ToString(), ((VideoAttributesBasic)sender).Owner);
    }
    else if (args.EventId == VideoAttributeEventIds.VerticalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("Vertical Resolution Feedback event occurred for {0} device\r\n", sender.ToString());
    }
}

//Method to handle the HDMI Output Stream CEC Change Events
public void DmRmc4Kz_HdmiOutputStreamCecEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", cecDevice.ToString());
    }
}

//Method to handle the HDMI Input Stream CEC Change Events
static void DmRmc4Kz_HdmiInputStreamCecEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", cecDevice.ToString());
    }
}

