static DmTxRx100Str _dmTxRx100Str;

public ControlSystemDmTxRx100Str()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DM-TXRX-100-STR CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

// Method to handle top level sig change events for DM-TXRX-100-STR Device.
static void DmTxRx100StrEventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == DMInputEventIds.SourceSyncEventId)
    {
        CrestronConsole.PrintLine("Sync detected on DM-TXRX-100-STR, SyncDetectedFeedback value is {0}", _dmTxRx100Str.HdmiIn.SyncDetectedFeedback.BoolValue);
    }
}

//Method to handle stream sig change events
static void DmTxRx100Str_StreamChangeEventHandler(Stream stream, StreamEventArgs args)
{
    if (args.EventId == DMInputEventIds.AudioFormatEventId)
    {
        CrestronConsole.PrintLine("{0} device Audio Format Feedback Event Occurred\r\n", stream.ToString());
    }
}

//Method to handle Video Attribute sig change events
static void DmTxRx100StrVideoAttributeEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} device Horizontal Resolution Feedback Event Occurred\r\n", sender.ToString());
    }
}

//Method to handle Cec sig change events
static void DmTxRx100StrCecChangeEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine(string.Format("Device: {0} - Cec Error Detected Status: {1}", _dmTxRx100Str.ToString(), _dmTxRx100Str.HdmiOut.StreamCec.ErrorFeedback.BoolValue));
    }
}

//Method to handle Device Info sig change events
static void DmTxRx100StrDeviceInfoChangeEventHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("{0} device Manufacturer Name Feedback Event Occurred\r\n", _dmTxRx100Str.HdmiOut.ConnectedDevice.ToString());
    }
}

//Method to create device
public void CreateDeviceCmd(String args)
{
    //Initialize the DM-TXRX-100-STR device for Ethernet Communication.
    _dmTxRx100Str = new DmTxRx100Str(0x18, this);

    _dmTxRx100Str.BaseEvent += DmTxRx100StrEventHandler;

    _dmTxRx100Str.HdmiIn.VideoAttributes.AttributeChange += DmTxRx100StrVideoAttributeEventHandler;
    _dmTxRx100Str.HdmiIn.StreamCec.CecChange += DmTxRx100StrCecChangeEventHandler;
    _dmTxRx100Str.HdmiIn.StreamChange += DmTxRx100Str_StreamChangeEventHandler;

    _dmTxRx100Str.HdmiOut.VideoAttributes.AttributeChange += DmTxRx100StrVideoAttributeEventHandler;
    _dmTxRx100Str.HdmiOut.StreamCec.CecChange += DmTxRx100StrCecChangeEventHandler;
    _dmTxRx100Str.HdmiOut.StreamChange += DmTxRx100Str_StreamChangeEventHandler;
    _dmTxRx100Str.HdmiOut.ConnectedDevice.DeviceInformationChange += DmTxRx100StrDeviceInfoChangeEventHandler;

    _dmTxRx100Str.SourceReceive.VideoAttributes.AttributeChange += DmTxRx100StrVideoAttributeEventHandler;
    _dmTxRx100Str.SourceReceive.StreamChange += DmTxRx100Str_StreamChangeEventHandler;

    _dmTxRx100Str.SourceTransmit.VideoAttributes.AttributeChange += DmTxRx100StrVideoAttributeEventHandler;
    _dmTxRx100Str.SourceTransmit.StreamChange += DmTxRx100Str_StreamChangeEventHandler;

    _dmTxRx100Str.Register();
}

static DmTx200C22G _dmTx20XC2;

public ControlSystemDmTx200C22G()
{
    //Register console commands 
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DmTx200C22G CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

public void CreateDeviceCmd(String args)
{
    //Initialize the DmTx200C22G device
    _dmTx20XC2 = new DmTx200C22G(0x16, this);

    _dmTx20XC2.HdmiInput.StreamCec.CecChange += StreamCecChangeEventHandler;
    _dmTx20XC2.HdmiInput.VideoAttributes.AttributeChange += VideoAttributeChange;
    _dmTx20XC2.HdmiInput.InputStreamChange += HdmiInputStreamChange;

    _dmTx20XC2.VgaInput.InputStreamChange += DmTx20XC2VgaStreamChange;
    _dmTx20XC2.VgaInput.VideoAttributes.AttributeChange += VideoAttributeChange;
    _dmTx20XC2.VgaInput.VideoControls.ControlChange += VgaVideoControlEventHandler;

    _dmTx20XC2.DmOutput.OutputStreamChange += DmOutputStreamChange;
    _dmTx20XC2.DmOutput.StreamCec.CecChange += StreamCecChangeEventHandler;
    _dmTx20XC2.DmOutput.ConnectedDevice.DeviceInformationChange += ConnectedDeviceOnDeviceInformationChange;

    _dmTx20XC2.UsbPort.UsbHidChange += UsbPortChange;
    _dmTx20XC2.BaseEvent += DmTx20XC2EventHandler;

    //Register the DmTx200C22G with the Control system
    //This will enable the gateway connection ("127.0.0.1") in the system at the 
    //ID specified above that the DmTx20XC2 will connect to.
    _dmTx20XC2.Register();

    if (_dmTx20XC2.Registered)
    {
        CrestronConsole.PrintLine("{0} device successfully registered\r\n", _dmTx20XC2.ToString());
    }
}

// Method to handle connected device sig change events for DM-TX-200-C2-2G device.
static void ConnectedDeviceOnDeviceInformationChange(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer Event occurred on {0}", connectedDevice.ToString());
    }
}

// Method to handle top level DM Out sig change events for DM-TX-200-C2-2G device.
static void DmOutputStreamChange(EndpointOutputStream outputStream, EndpointOutputStreamEventArgs args)
{
    if (args.EventId == EndpointOutputStreamEventIds.HotplugDetectedEventId)
    {
        CrestronConsole.PrintLine("DM Out Hot Plug Detected Feedback Event occurred on {0}", outputStream.ToString());
    }
}

// Method to handle top level stream sec sig change events for DM-TX-200-C2-2G device.
static void StreamCecChangeEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("Cec Message Received Event Received for {0}\r\n", cecDevice.ToString());
    }
}

// Method to handle HDMI input stream sig change events for DM-TX-200-C2-2G device.
static void HdmiInputStreamChange(EndpointInputStream inputStream, EndpointInputStreamEventArgs args)
{
    if (args.EventId == EndpointInputStreamEventIds.HdcpSupportOnFeedbackEventId)
    {
        CrestronConsole.PrintLine("Hdcp Support On Feedback Event Received for {0}\r\n", inputStream.ToString());
    }
}

// Method to handle video attribute sig change events for DM-TX-200-C2-2G device.
static void VideoAttributeChange(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("Horizontal Resolution Feedback Event occurred on {0}", _dmTx20XC2.ToString());
    }
}

// Method to handle VGA stream change sig change events for DM-TX-200-C2-2G device.
static void DmTx20XC2VgaStreamChange(EndpointInputStream inputstream, EndpointInputStreamEventArgs args)
{
    if (args.EventId == EndpointInputStreamEventIds.AudioFormatFeedbackEventId)
    {
        CrestronConsole.PrintLine("Audio Format Feedback Event ID is received for {0}", inputstream);
    }
}

// Method to handle VGA video control sig change events for DM-TX-200-C2-2G device.
static void VgaVideoControlEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoControlsEventIds.BrightnessFeedbackEventId)
    {
        CrestronConsole.PrintLine("Brightness Feedback Event occurred on {0}", _dmTx20XC2.ToString());
    }
}

// Method to handle USB port change sig change events for DM-TX-200-C2-2G device.
static void UsbPortChange(UsbHidPort usbHidPort, UsbHidEventArgs args)
{
    if (args.EventId == UsbHidEventIds.DeviceCountEventId)
    {
        CrestronConsole.PrintLine("Device Count Event occurred on {0}", usbHidPort.ToString());
    }
}

// Method to handle top level sig change events for DM-TX-200-C2-2G device.
static void DmTx20XC2EventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == EndpointTransmitterBase.VideoSourceFeedbackEventId)
    {
        CrestronConsole.PrintLine("Video Source Feedback Event occurred on {0}", _dmTx20XC2.ToString());
    }
}

//Creating a new instance of the switch.
var dmSwitch = new HdMd8x14k(0x03, this);

//Registering the switch.
