dmSwitch.DMSystemChange += DmSwitchDMSystemChange;

static Dmb4KOHD _dmb4kOHd;
static DmMd64x64 _dmSwitcher;

public ControlSystemDmb4kOHd()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DMB-4K-O-HD CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(SetDisplayModeCmd, "SetDisplayMode", "This is a DMB-4K-O-HD SetDisplayMode command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to set the Output Scaler Display Mode for Card 1.
public void SetDisplayModeCmd(String args)
{
    if (args == "0")
    {
        _dmb4kOHd.Cards[1].Hdmi4kOutput.Scaler.DisplayMode = EndpointScalerOutput.eDisplayMode.Maintain;
    }
    else if (args == "1")
    {
        _dmb4kOHd.Cards[1].Hdmi4kOutput.Scaler.DisplayMode = EndpointScalerOutput.eDisplayMode.Stretch;
    }
    else if (args == "2")
    {
        _dmb4kOHd.Cards[1].Hdmi4kOutput.Scaler.DisplayMode = EndpointScalerOutput.eDisplayMode.OneToOne;
    }
    else if (args == "3")
    {
        _dmb4kOHd.Cards[1].Hdmi4kOutput.Scaler.DisplayMode = EndpointScalerOutput.eDisplayMode.Zoom;
    }
    else
    {
        _dmb4kOHd.Cards[1].Hdmi4kOutput.Scaler.DisplayMode = EndpointScalerOutput.eDisplayMode.None;
    }
}

public void CreateDeviceCmd(String args)
{
    //For Switcher
    _dmSwitcher = new DmMd64x64(0x20, this);
    _dmb4kOHd = new Dmb4KOHD(1, _dmSwitcher);

    //Register the DM Switcher with the Control System
    _dmSwitcher.Register();

    foreach (var card in _dmb4kOHd.Cards)
    {
        card.Hdmi4kOutput.StreamCec.CecChange += DmbHdmi4kOutputCard_StreamCecEventHandler;
        card.Hdmi4kOutput.Scaler.OutputChange += DmbHdmi4kOutputCard_ScalerEventHandler;
        card.Hdmi4kOutput.connectedDevice.DeviceInformationChange += DmbHdmi4kOutputCard_DeviceInfoEventHandler;
        card.SourceVideoAttributes.AttributeChange += DmbHdmi4kOutputCard_SourceVideoAttributeEventHandler;
    }

    _dmSwitcher.DMOutputChange += Dmb4kIC_OutputSigChangeEventHandler;
}

//Method to handle the DMOutput ConnectedDevice sig change events
static void DmbHdmi4kOutputCard_DeviceInfoEventHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("{0} Manufacturer Event Occurred, Manufacturer is {1}", connectedDevice.ToString(), connectedDevice.Manufacturer.StringValue);
    }
}

//Method to handle the DMOutput Scaler sig change events
static void DmbHdmi4kOutputCard_ScalerEventHandler(EndpointScalerOutput scalerOutput, ScalerOutputEventArgs args)
{
    if (args.EventId == ScalerOutputEventIds.UnderscanModeFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Underscan Mode Feedback Event Occurred, UnderscanModeFeedback is {1}\r\n", scalerOutput.ToString(), scalerOutput.UnderscanModeFeedback);
    }
}

//Method to handle Blade HDMI port Source VideoAttributes sig change events
static void DmbHdmi4kOutputCard_SourceVideoAttributeEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} device Horizontal Resolution Feedback Event Occurred, HorizontalResolutionFeedback value is {1}\r\n", sender.ToString(), ((VideoAttributesEnhanced)sender).HorizontalResolutionFeedback.UShortValue);
    }
}

//Method to handle Blade HDMI port Cec sig change events
static void DmbHdmi4kOutputCard_StreamCecEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("{0} Message Received Event Occurred, Received Message is {1}\r\n", cecDevice.ToString(), cecDevice.Send.StringValue);
    }
}

//Method to handle Blade DM Output sig change events
static void Dmb4kIC_OutputSigChangeEventHandler(Switch device, DMOutputEventArgs args)
{
    if (args.EventId == DMOutputEventIds.OnlineFeedbackEventId)
    {
        CrestronConsole.PrintLine("Online Feedback event occurred for {0}, OnlineFeedback status is {1}", device.ToString(), device.Inputs[args.Number].OnlineFeedback.BoolValue);
    }
    else if (args.EventId == DMOutputEventIds.PresentFeedbackEventId)
    {
        CrestronConsole.PrintLine("Present Feedback event occurred for {0}, PresentFeedback status is {1}", device.ToString(), device.Inputs[args.Number].PresentFeedback.BoolValue);
    }
    else if (args.EventId == DMOutputEventIds.TemperatureEventId)
    {
        CrestronConsole.PrintLine("Temperature event occurred for {0}, TemperatureFeedback value is {1}", device.ToString(), _dmb4kOHd.Cards[args.Number].TemperatureFeedback.UShortValue);
    }
}

const uint NumberOfHdmiInputPorts = 1;
HdTxC101C1GE _hdtxxxxce;
static HdRx4k210CEPoe _dmSwitcher;

public ControlSystemHdTxC101C1GE()
{
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is an HD-TXC-101-C(-1G)-E CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

public void CreateDeviceCmd(String args)
{
    _dmSwitcher = new HdRx4k210CEPoe(0x11, this);
    _hdtxxxxce = new HdTxC101C1GE(_dmSwitcher.DmLiteInputs[1]);

    _hdtxxxxce.BaseEvent += HdtxxxxceBaseEvent;

    for (uint i = 1; i <= NumberOfHdmiInputPorts; i++)
    {
        _hdtxxxxce.HdmiInputs[i].InputStreamChange += HdmiInputStreamChangeEventHandler;
        _hdtxxxxce.HdmiInputs[i].VideoAttributes.AttributeChange += HdmiInputGenericEventHandler;
    }
    _hdtxxxxce.ComPorts[1].SerialDataReceived += OnSerialDataReceived;
    Thread.MaxNumberOfUserThreads = 20;

    _dmSwitcher.Register();
}

public void HdtxxxxceBaseEvent(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == EndpointTransmitterBase.HdmiInNameFeedbackEventId)
    {
        CrestronConsole.PrintLine("HDMI In Name Feedback event for {0} occurred\r\n", device);
    }
}

void HdmiInputStreamChangeEventHandler(EndpointInputStream inputStream, EndpointInputStreamEventArgs args)
{
    if (args.EventId == EndpointInputStreamEventIds.SyncDetectedFeedbackEventId)
    {
        CrestronConsole.PrintLine("SyncDetectedFBEvent event for occurred\r\n");
    }
}

void HdmiInputGenericEventHandler(object sender, Crestron.SimplSharpPro.DeviceSupport.GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("HorizontalResolutionFeedback event occurred\r\n");
    }
}

static void OnSerialDataReceived(ComPort receivingComPort, ComPortSerialDataEventArgs args)
{
    CrestronConsole.PrintLine("Serial data received is {0}", args.SerialData);
}

static HdWp4k401C _hdWp4k401C;
static Tt1xx _tt1x1;
static Tt1xx _tt1x2;
static Tt1xx _tt1x3;
static Tt1xx _tt1x4;

public ControlSystemHdWp4k401C()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a HD-WP-4K-401-C CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create HD-WP-4K-401-C device.
public void CreateDeviceCmd(String args)
{
    //Initialize the HD-WP-4K-401-C device
    _hdWp4k401C = new HdWp4k401C(0x18, this);

    _tt1x1 = new Tt1xx(1, _hdWp4k401C);
    _tt1x2 = new Tt1xx(2, _hdWp4k401C);
    _tt1x3 = new Tt1xx(3, _hdWp4k401C);
    _tt1x4 = new Tt1xx(4, _hdWp4k401C);

    _tt1x1.ButtonStateChange += Tt1xxButtonStateChangeEvent;
    _tt1x2.ButtonStateChange += Tt1xxButtonStateChangeEvent;
    _tt1x3.ButtonStateChange += Tt1xxButtonStateChangeEvent;
    _tt1x4.ButtonStateChange += Tt1xxButtonStateChangeEvent;

    //Register the HD-WP-4K-401-C with the Control System
    //This will enable the gateway connection ("127.0.0.1") in the system at the 
    //ID specified above that the HD-WP-4K-401-C will connect to.
    _hdWp4k401C.Register();

    _hdWp4k401C.BaseEvent += HdWp4k401CSigChange;
    _hdWp4k401C.HdWpWindowLayout.WindowLayoutChange += HdWpWindowLayoutChange;
    _hdWp4k401C.Outputs.HdmiOut.StreamChange += HdWp4k401COutputsHdmiOutSigChange;
}

//Method to handle the Window Layout sig change events
static void HdWpWindowLayoutChange(object device, GenericEventArgs args)
{
    if (args.EventId == WindowLayoutEventIds.WindowTitlesEnabledEventId)
    {
        CrestronConsole.PrintLine("Event {0} occurred for {1}", WindowLayoutEventIds.WindowTitlesEnabledEventId, _hdWp4k401C.HdWpWindowLayout.ToString());
    }
}

//Method to handle the TT1XX Button State change events
static void Tt1xxButtonStateChangeEvent(GenericBase device, ButtonEventArgs args)
{
    CrestronConsole.PrintLine("{0} device {1} state changed to {2}\r\n", device.ToString(),args.Button.Name, args.NewButtonState);
}

//Method to handle the Outputs-HDMI Output sig change events
static void HdWp4k401COutputsHdmiOutSigChange(Stream stream, StreamEventArgs args)
{
    if (args.EventId == DMOutputEventIds.HotplugDetectedEventId)
    {
        CrestronConsole.PrintLine("Event {0} occurred for {1}", DMOutputEventIds.HotplugDetectedEventId, _hdWp4k401C.Outputs.HdmiOut.ToString());
    }
}

//Method to handle the Outputs sig change events
static void HdWp4k401CSigChange(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == DMOutputEventIds.ResolutionEventId)
    {
        CrestronConsole.PrintLine("Event {0} occurred for {1}", DMOutputEventIds.ResolutionEventId, _hdWp4k401C.Outputs.ToString());
    }
}

