static Dmb4kIC _dmb4kIC;
static DmMd64x64 _dmswitcher;

public ControlSystemDmb4kIC()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DMB-4K-I-C CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(SetHdcpCapabilityCmd, "SetHdcpCapability", "This is a DMB-4K-I-C SetHdcpCapability command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the DMB-4K-I-C Input Blade Device.
public void CreateDeviceCmd(String args)
{
     //For Switcher
    _dmswitcher = new DmMd64x64(0x20, this);
    _dmb4kIC = new Dmb4kIC(1, _dmswitcher);

    //Register the DM Switcher with the Control System
    _dmswitcher.Register();

    foreach (var card in _dmb4kIC.Cards)
    {
        card.DmInput.VideoAttributes.AttributeChange += DmbDmInputCard_VideoAttributeEventHandler;
        card.DmInput.StreamCec.CecChange += DmbDmInputCard_StreamCecEventHandler;
    }

    _dmswitcher.DMInputChange += Dmb4kIC_InputSigChangeEventHandler;
}

//Method to set the HDCP capability value to card 1 DM Input on the DMB-4K-I-C blade
public void SetHdcpCapabilityCmd(String args)
{
    if (args == "0")
    {
        _dmb4kIC.Cards[1].DmInput.HdcpCapability = eHdcpCapabilityType.HdcpSupportOff;
    }
    else if (args == "1")
    {
        _dmb4kIC.Cards[1].DmInput.HdcpCapability = eHdcpCapabilityType.Hdcp2xSupport;
    }
    else if (args == "2")
    {
        _dmb4kIC.Cards[1].DmInput.HdcpCapability = eHdcpCapabilityType.Hdcp1xSupport;
    }
 }

//Method to handle the Blade VideoAttributes Sig Change events
static void DmbDmInputCard_VideoAttributeEventHandler(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.InterlacedFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} device Interlaced Feedback Event Occurred, InterlacedFeedback property value is {1}\r\n", sender.ToString(), ((VideoAttributesEnhanced)sender).InterlacedFeedback.BoolValue);
    }
}

//Method to handle the Blade StreamCec Sig Change events
static void DmbDmInputCard_StreamCecEventHandler(Cec cecdevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("{0} Message Received Event Occurred, Received Message is {1}\r\n", cecdevice.ToString(), cecdevice.Send.StringValue);
    }
}

//Method to handle the Blade DM Input Sig Change events
static void Dmb4kIC_InputSigChangeEventHandler(Switch device, DMInputEventArgs args)
{
    if (args.EventId == DMInputEventIds.OnlineFeedbackEventId)
    {
        CrestronConsole.PrintLine("Online Feedback event occurred for {0}, OnlineFeedback status is {1}", device.ToString(), device.Inputs[args.Number].OnlineFeedback.BoolValue);
    }
    else if (args.EventId == DMInputEventIds.PresentFeedbackEventId)
    {
        CrestronConsole.PrintLine("Present Feedback event occurred for {0}, PresentFeedback status is {1}", device.ToString(), device.Inputs[args.Number].PresentFeedback.BoolValue);
    }
    else if (args.EventId == DMInputEventIds.HdcpCapabilityFeedbackEventId)
    {
        CrestronConsole.PrintLine("Hdcp Receive Capability event occurred for {0}, HdcpCapabilityFeedback value is {1}", device.ToString(), _dmb4kIC.Cards[args.Number].DmInput.HdcpCapabilityFeedback);
    }
    else if (args.EventId == DMInputEventIds.TemperatureEventId)
    {
        CrestronConsole.PrintLine("Temperature event occurred for {0}, TemperatureFeedback value is {1}", device.ToString(), _dmb4kIC.Cards[args.Number].TemperatureFeedback.UShortValue);
    }
    else if (args.EventId == DMInputEventIds.EndpointOnlineEventId)
    {
        CrestronConsole.PrintLine("Endpoint Online event occurred for {0}, EndpointOnlineFeedback value is {1}", device.ToString(), _dmb4kIC.Inputs[args.Number].EndpointOnlineFeedback);
    }
}

