example.code:
static Dmc4kzCoHdSingle _dmc4kzCoHd;
static DmMDMnxn _dmSwitcher;

public ControlSystemDmc4kzCoHd()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceAsSwitcherDmOutputCmd, "CreateDevSwDmOutput", "This is a DMC-4KZ-CO-HD CreateDevSwDmOutput command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create the device as the Switcher DM output
public void CreateDeviceAsSwitcherDmOutputCmd(String args)
{
    //For Switcher
    _dmSwitcher = new DmMd8x8(0x51, this);
    _dmc4kzCoHd = new Dmc4kzCoHdSingle(1, _dmSwitcher);

    //Register the DM Switcher with the Control System
    _dmSwitcher.Register();

    _dmSwitcher.DMOutputChange += DmSwitcher_OutputEventHandler;

    _dmc4kzCoHd.Card1.DmOutput.ConnectedDevice.DeviceInformationChange += DmRmc4Kz_DeviceInfoEventHandler;
    _dmc4kzCoHd.Card1.DmOutput.StreamCec.CecChange += Dmc4kzCoHd_StreamCecEventHandler;
    _dmc4kzCoHd.Card1.HdmiOutput.ConnectedDevice.DeviceInformationChange += DmRmc4Kz_DeviceInfoEventHandler;
    _dmc4kzCoHd.Card1.HdmiOutput.StreamCec.CecChange += Dmc4kzCoHd_StreamCecEventHandler;

    _dmc4kzCoHd.Card2.DmOutput.ConnectedDevice.DeviceInformationChange += DmRmc4Kz_DeviceInfoEventHandler;
    _dmc4kzCoHd.Card2.DmOutput.StreamCec.CecChange += Dmc4kzCoHd_StreamCecEventHandler;
}

//Method to handle the DM Output events
public void DmSwitcher_OutputEventHandler(Switch device, DMOutputEventArgs args)
{
    if (args.EventId == DMOutputEventIds.OnlineFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} device Online Feedback Event occurred, Card No. {1}\r\n", device.ToString(), args.Number);
    }
    else if (args.EventId == DMOutputEventIds.PresentFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} device Present Feedback Event occurred, Card No. {1}\r\n", device.ToString(), args.Number);
    }
    else if (args.EventId == DMOutputEventIds.DisabledByHdcpEventId)
    {
        CrestronConsole.PrintLine("{0} device Disabled By Hdcp Event occurred, Card No. {1} Stream {2}\r\n", device.ToString(), args.Number, args.Stream);
    }
}

//Method to handle the HDMI Output and DM Output Device Information change events
public void DmRmc4Kz_DeviceInfoEventHandler(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer event occurred for {0} device of {1}\r\n", connectedDevice.ToString(), connectedDevice.Owner);
        CrestronConsole.PrintLine("Manufacturer Feedback Value is {0}\r\n", connectedDevice.Manufacturer.UShortValue);
    }
    else if (args.EventId == ConnectedDeviceEventIds.NameEventId)
    {
        CrestronConsole.PrintLine("Name event occurred for {0} device\r\n", connectedDevice.ToString());
    }
    else if (args.EventId == ConnectedDeviceEventIds.PreferredTimingEventId)
    {
        CrestronConsole.PrintLine("Preferred Timing event occurred for {0} device\r\n", connectedDevice.ToString());
    }
    else if (args.EventId == ConnectedDeviceEventIds.SerialNumberEventId)
    {
        CrestronConsole.PrintLine("Serial Number event occurred for {0} device\r\n", connectedDevice.ToString());
    }
}

//Method to handle the HDMI Output and DM Output Stream CEC change events
public void Dmc4kzCoHd_StreamCecEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.ErrorFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Error Feedback Event Occurred\r\n", cecDevice.ToString());
    }
    else if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("{0} Message Received Event Occurred\r\n", cecDevice.ToString());
    }
}

