static DmTx4kz100C1G _dmTx4kz100C1G;

public ControlSystemDmTx4kz100C1G()
{
    //Register console commands 
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DmTx4kz100C1G CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

public void CreateDeviceCmd(String args)
{
    //Initialize the DmTx4kz100C1G device
    _dmTx4kz100C1G = new DmTx4kz100C1G(0x16, this);

    _dmTx4kz100C1G.HdmiInput.StreamCec.CecChange += StreamCecChangeEventHandler;
    _dmTx4kz100C1G.HdmiInput.VideoAttributes.AttributeChange += VideoAttributeChange;
    _dmTx4kz100C1G.HdmiInput.InputStreamChange += HdmiInputStreamChange;

    _dmTx4kz100C1G.DmOutput.OutputStreamChange += DmOutputStreamChange;
    _dmTx4kz100C1G.DmOutput.StreamCec.CecChange += StreamCecChangeEventHandler;
    _dmTx4kz100C1G.DmOutput.ConnectedDevice.DeviceInformationChange += ConnectedDeviceOnDeviceInformationChange;

    _dmTx4kz100C1G.ComPorts[1].SerialDataReceived += OnSerialDataReceived;

    _dmTx4kz100C1G.Register();

    if (_dmTx4kz100C1G.Registered)
    {
        CrestronConsole.ConsoleCommandResponse("{0} device successfully registered\r\n", _dmTx4kz100C1G.ToString());
    }
}

static void OnSerialDataReceived(ComPort receivingComPort, ComPortSerialDataEventArgs args)
{
    CrestronConsole.PrintLine("Serial data received is {0}", args.SerialData);
}

static void ComPort_PropertyChanged(ComPort receivingcomport, ComPortPropertyEventArgs args)
{
    if (args.Property == eComPortProperty.CD)
    {
        CrestronConsole.PrintLine("DM-RMC-4K-SCALER-C COM Port {0} CD Changed to {1}", receivingcomport.ID, receivingcomport.CD);
    }
}

public void DmRmc4K100C1G_BaseEvent(GenericBase device, BaseEventArgs args)
{
    CrestronConsole.PrintLine("DmRmc4K100C1G_BaseEvent occurred. Event ID is {0} device index is {1}.\r\n", args.EventId, args.Index);
}

// Method to handle connected device sig change events for DM-TX-4KZ-100-C-1G device.
static void ConnectedDeviceOnDeviceInformationChange(ConnectedDeviceInformation connectedDevice, ConnectedDeviceEventArgs args)
{
    if (args.EventId == ConnectedDeviceEventIds.ManufacturerEventId)
    {
        CrestronConsole.PrintLine("Manufacturer Feedback Event occurred on {0} event ID is {1}", connectedDevice.ToString(), args.EventId);
    }
}

// Method to handle top level DM Out sig change events for DM-TX-4KZ-100-C-1G device.
static void DmOutputStreamChange(EndpointOutputStream outputStream, EndpointOutputStreamEventArgs args)
{
    if (args.EventId == EndpointOutputStreamEventIds.DmPassThroughModeEnabledFeedbackEventId)
    {
        CrestronConsole.PrintLine("DM Pass Through Mode Enabled Feedback Event occurred on {0} event ID is {1} stream string is {2}", outputStream.ToString(), args.EventId, outputStream.ToString());
    }
}

// Method to handle top level stream sec sig change events for DM-TX-4KZ-100-C-1G device.
static void StreamCecChangeEventHandler(Cec cecDevice, CecEventArgs args)
{
    if (args.EventId == CecEventIds.CecMessageReceivedEventId)
    {
        CrestronConsole.PrintLine("Cec Message Received Feedback Event occurred on {0} event ID is {1} cec string is {2}", cecDevice.Owner.ToString(), args.EventId, cecDevice.ToString());
    }
}

// Method to handle HDMI input stream sig change events for DM-TX-4KZ-100-C-1G device.
static void HdmiInputStreamChange(EndpointInputStream inputStream, EndpointInputStreamEventArgs args)
{
    if (args.EventId == EndpointInputStreamEventIds.AudioFormatFeedbackEventId)
    {
        CrestronConsole.PrintLine("Audio Format Feedback Event occurred on {0} event ID is {1}", inputStream.ToString(), args.EventId);
    }
}

// Method to handle video attribute sig change events for DM-TX-4KZ-100-C-1G device.
static void VideoAttributeChange(object sender, GenericEventArgs args)
{
    if (args.EventId == VideoAttributeEventIds.HorizontalResolutionFeedbackEventId)
    {
        CrestronConsole.PrintLine("Horizontal Resolution Feedback Event occurred on {0} event ID is {1}", sender.ToString(), args.EventId);
    }
}

// Method to handle top level sig change events for DM-TX-4KZ-100-C-1G Device.
static void DmTx20XC2EventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == EndpointTransmitterBase.VideoSourceFeedbackEventId)
    {
        CrestronConsole.PrintLine("Video Source Feedback Event occurred on {0} event ID is {1}", device.ToString(), args.EventId);
    }
}

DmXioDirectorBase _xioDirector = new DmXioDirector160(0x03, this);
if(_xioDirector.DmXioDirectorType == eDmXioDirectorType.DmXioDirector160)
{
    DmXioDirector160 xio160 = (DmXioDirector160)_xioDirector;
    // Use xio160 as your class to access functions.
}

static DmXioDirectorBase _xioDirector;

public ControlSystemDmXioDirectorXXX()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a DM-XIO-DIRECTOR-XXX CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(CreateDomainsCmd, "CreateDomains", "This is a DM-XIO-DIRECTOR-XXX CreateDomains command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(DisposeDomainsCmd, "DisposeDomains", "This is a DM-XIO-DIRECTOR-XXX DisposeDomains command", ConsoleAccessLevelEnum.AccessOperator);

    CrestronConsole.AddNewConsoleCommand(CreateEndpointsCmd, "CreateEndpoints", "This is a DM-XIO-DIRECTOR-XXX CreateEndpoints command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(DisposeEndpointsCmd, "DisposeEndpoints", "This is a DM-XIO-DIRECTOR-XXX DisposeEndpoints command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to dispose endpoints
public void DisposeEndpointsCmd(string args)
{
    foreach (var domain in _xioDirector.Domain)
    {
        foreach (var endpoint in domain.TransmitterEndpoints)
        {
            endpoint.Dispose();
            CrestronEnvironment.AllowOtherAppsToRun();
        }
        CrestronConsole.PrintLine("Total Number Of Transmitter Endpoints {0}- For Domain {1}", _xioDirector.TotalNumberOfTransmitterEndpoints, domain.ToString());
        foreach (var endpoint in domain.ReceiverEndpoints)
        {
            endpoint.Dispose();
            CrestronEnvironment.AllowOtherAppsToRun();
        }
        CrestronConsole.PrintLine("Total Number Of Receiver Endpoints {0} - For Domain {1}", _xioDirector.TotalNumberOfReceiverEndpoints, domain.ToString());
    }
}

//Method to create endpoints
public void CreateEndpointsCmd(string args)
{
    foreach (var domain in _xioDirector.Domain)
    {
        for (uint i = 1; i <= 10; i++)
        {
            var endpoint = new DmNvx350C(i, domain, true);
            CrestronEnvironment.AllowOtherAppsToRun();
        }
        for (uint i = 1; i <= 10; i++)
        {
            var txrx = new DmTxRx100Str(i, domain, false);
            CrestronEnvironment.AllowOtherAppsToRun();
        }
    }
}

//Method to create domains
public void CreateDomainsCmd(String args)
{
    uint i;
    for (i = 1; i <= 10; i++)
    {
        var domain = new DmXioDirectorBase.DmXioDomain(i, _xioDirector);
        CrestronEnvironment.AllowOtherAppsToRun();
    }
    CrestronConsole.ConsoleCommandResponse("{0} Domains added successfully\r\n", i - 1);
}

//Method to dispose the domains created
public void DisposeDomainsCmd(String args)
{
    foreach (var domain in _xioDirector.Domain)
    {
        domain.Dispose();
    }
    CrestronConsole.ConsoleCommandResponse("Domains disposed successfully\r\n");
}

//Method to create the DM-XIO-DIRECTOR device object
public void CreateDeviceCmd(String args)
{
    _xioDirector = new DmXioDirectorEnterprise(220, this);

    _xioDirector.DeviceFunctions.DeviceFunctionChange += XioDirectorDeviceFunctionChangeEvent;
    _xioDirector.Register();

    CrestronConsole.ConsoleCommandResponse("{0} device successfully registered\r\n", _xioDirector.ToString());
}

//Method to handle the Device Functions Sig change event of DM-XIO-DIRECTOR-XXX
static void XioDirectorDeviceFunctionChangeEvent(object sender, GenericEventArgs args)
{
    if (args.EventId == DmXioDirectorBase.DeviceFailedEventId)
    {
        CrestronConsole.PrintLine("{0} Device Failed event occurred for Device Functions. EquipmentType is {1} Value is {2}.\r\n", _xioDirector, args.Index, _xioDirector.DeviceFunctions.DeviceStatusCollection[args.Index].FailedFeedback.BoolValue);
    }
    else if (args.EventId == DmXioDirectorBase.SystemTemperatureFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} System Temperature Feedback event occurred for Device Functions. Value is {1}.\r\n", _xioDirector, _xioDirector.DeviceFunctions.SystemTemperatureFeedback.UShortValue);
    }
}
