example.code:
 /// Add our assets to the Fusion room. Each Asset can be a variety of types 
 /// For more info on each asset type, go to Fusion's SSI guide.
 /// Assets store information about each devices.
 /// For most use casses, static assets will be used.
 /// See FusionRoom example for initialization steps.
 private static bool addAsset(FusionRoom room)
 {
     bool returnValue = false;

     try
     {
         // IMPORTANT: when adding assets, the instance id must be unique, if it is not
         // fusion will override the old asset with the most recent asset with the same ID.  

         // Add an asset to the room with the type Static asset, with the type of Display.
         room.AddAsset(eAssetType.StaticAsset, _displayId, "mainDisplay", "Display", "0005");

         //return the status of the function's execution.
         returnValue = true;
     }
     catch (Exception e)
     {
         ErrorLog.Error("Error while creating Asset: {0}, {1}", e.Message.ToString(), e.StackTrace);

         // Flag our return value as false since execution has reached the exception block.
         returnValue = false;
     }

     // Return the boolean indicating the success of the function's execution.
     return returnValue;
 }

 // Returns a FusionStaticAsset object if type casting is successful.
 // If not then null is returned.
 public static FusionStaticAsset GetDisplay(FusionRoom room)
 {		
	    try
	    {
	        // Type casting the Asset baseClass to the StaticAsset class
	        // as that is what was specified on time of creation.
		    return room.UserConfigurableAssetDetails[_displayId].Asset as FusionStaticAsset;
	    }
	    catch (Exception e)
	    {
		    ErrorLog.Error("Error while trying to return FusionAsset at index: {0}", _displayId, e.StackTrace);
	    }

	    return null;
 }

 // Tracks the usage while device is detected as online.
 // These are called within other functions in the example.
 // However, call them at your discretion as there are multiple scenarios where
 // it would be a good idea to log device's usage time.
 public static void DisplayDeviceUsage(FusionRoom room, DateTime displayStartTime)
 {
     // Subtract the time the device came online from the current time.
     TimeSpan elapsedTime = CrestronEnvironment.GetLocalTime().Subtract(displayStartTime);

     // Report how long the display was used to Fusion.
     // for Display usage tracking.
     room.DisplayUsage.InputSig.UShortValue += (ushort)elapsedTime.TotalHours;

     // If the room is object is not initialized or the elapsed time is less than 1 minute 
     // Don't send usage data.
     if (room != null && (int)elapsedTime.TotalHours > 0)
     {
         // Construct the time stamp that will be send over to Fusion.    
         string time = elapsedTime.Hours + ":" + elapsedTime.Minutes + ":" + elapsedTime.Seconds;

         // Log some information about the usage to the control system.
         ErrorLog.Notice("Time: {0}", time);
         ErrorLog.Notice("Total elapsed hours: {0}", (int)elapsedTime.TotalHours);

         // Construct the date stamp that will be sent over to Fusion.
         string date = DateTime.Now.Year + "-" + DateTime.Now.Month + "-" + DateTime.Now.Day;

         // Construct the data that will be sent over to Fusion.
         room.DeviceUsage.InputSig.StringValue = "USAGE||" + date + "||" + time + "||TIME||DISPLAY||mainDisplay||-||" + (int)elapsedTime.TotalHours + "||-||-||-||";
     }
     else
     {
         ErrorLog.Notice("Time active too small to track for display.");
     }
 }

// Private Member variables.
#region Private member variables
private static FusionRoom _room;

/// Initializes all objects and event listeners.
/// This function should be called within you ControlSystem class.
public static bool InitializeDevice(CrestronControlSystem controller)
{
    bool returnValue = false;

    // If the controller is null, then return and report an error to the control system log.
    if (controller == null)
    {
        ErrorLog.Error("Error with control system. Aborting initialization");
        return returnValue;
    }

    try
    {
        // Construct our room object with IP_ID, control system, name, and GUID. 
        _room = new FusionRoom(0x04, controller, "conference room", "0000");

        // Set the description of the room. This will appear on Fusion's UI.
        _room.Description = "first floor conference room.";

        // Add our assets to the Fusion room, check if function execution was successful.
        if (addAsset())
        {
            ErrorLog.Notice("Assets created at: {0}", DateTime.Now.ToString());
        }
        // If there is an error, send a message to the log.
        else
            ErrorLog.Notice("Assets not created.");

        // Set up Fusion Room event handlers.
        _room.FusionAssetStateChange += new FusionAssetStateEventHandler(room_FusionAssetStateChange);

        _room.FusionStateChange += new FusionStateEventHandler(room_FusionStateChange);

        _room.OnlineStatusChange += new OnlineStatusChangeEventHandler(OnlineStatusChange);

        // Reserve signals being using.
        _room.ExtenderFusionRoomDataReservedSigs.Use();

        _room.ExtenderFusionRoomDataReservedSigs.DeviceExtenderSigChange += new DeviceExtenderJoinChangeEventHandler(ExtenderFusionRoomDataReservedSigs_DeviceExtenderSigChange);

        // Send a message to Fusion log that the system has come online.
        _room.LogText.InputSig.StringValue = "System initializing at: " + DateTime.Now.ToString();

        // After the room, and assets are setup then we can register our fusion room.
        // IMPORTANT, do not generate the RVI file after devices registration. There will be problems with your Fusion objects being loaded.
        FusionRVI.GenerateFileForAllFusionDevices();

        // Register the room. With Fusion.
        if (_room.Register() != eDeviceRegistrationUnRegistrationResponse.Success)
        {
            ErrorLog.Error("Could not register Fusion room: {0}", _room.Name);
        }

        // Finally, set the returnValue to true as execution has reached the end of the function.
        returnValue = true;
    }
    catch (Exception e)
    {
        ErrorLog.Error("Error while Initializing fusion room: {0}, {1}", e.Message.ToString(), e.StackTrace);
        // If an error occurs during Initialization, flag our return values as false. 
        returnValue = false;
    }

    // Return the status of the function execution.
    return returnValue;
}

/// Add our assets to the Fusion room. Each Asset can be a variety of types 
/// For more info on each asset type, go to Fusion's SSI guide.
/// Assets store information about each devices (1:1).
private static bool addAsset()
{
    bool returnValue = false;

    try
    {
        // IMPORTANT: when adding assets, the instance id must be unique, if it is not
        // fusion will override the old asset with the most recent asset with the same ID.  

        // Add an asset to the room with the type of Static Asset. With the type of TouchPanel.
        _room.AddAsset(eAssetType.StaticAsset, _touchPanelId, "TSW752", "Touchpanel", "0003");

        // Add an asset to the room with the type of Static Asset. With the type of Computer.
        _room.AddAsset(eAssetType.StaticAsset, _laptopId, "Thinkpad", "Computer", "0004");

        // Add an asset to the room with the type Static asset, with the type of Display.
        _room.AddAsset(eAssetType.StaticAsset, _displayId, "mainDisplay", "Display", "0005");

        //return the status of the function's execution.
        returnValue = true;
    }
    catch (Exception e)
    {
        ErrorLog.Error("Error while creating Asset: {0}, {1}", e.Message.ToString(), e.StackTrace);

        // Flag our return value as false since execution has reached the exception block.
        returnValue = false;
    }

    // Return the boolean indicating the success of the function's execution.
    return returnValue;
}

// Event handler for checking when a room comes oneline
private static void OnlineStatusChange(GenericBase currentDevice, OnlineOfflineEventArgs args)
{
    if (args.DeviceOnLine)
    {
        ErrorLog.Notice("{0} is online at: {1}", currentDevice.Description, DateTime.Now.ToString());
    }
    else
    {
        ErrorLog.Notice("{0} is offline", currentDevice.Description);
    }
}

// Event listener for device extender.
private static void ExtenderFusionRoomDataReservedSigs_DeviceExtenderSigChange(DeviceExtender currentDeviceExtender, SigEventArgs args)
{
    ErrorLog.Notice("extender signal change: {0}", args.Sig.Name);
}

// State change event handler for Fusion Rooom.
private static void room_FusionStateChange(FusionBase device, FusionStateEventArgs args)
{
    // goes through each event id.
    switch (args.EventId)
    {
        // If display power is turned off then we must flag it as offline and turn off the display.
        case FusionEventIds.DisplayPowerOffReceivedEventId:

            break;

        // If power to the display is turned on then we turn our display on and 
        // flag it as online.
        case FusionEventIds.DisplayPowerOnReceivedEventId:

            break;

        // If system power is turned on then we mark is as on.
        case FusionEventIds.SystemPowerOnReceivedEventId:

            break;

        // If the system is turned off from fusion then we mark it as offline and 
        // turn off all connected devices.
        case FusionEventIds.SystemPowerOffReceivedEventId:

            break;

       }

       // print the EventId sent to the control system by fusion.
       CrestronConsole.PrintLine("Event Id: {0}", args.EventId.ToString());
   }
}
