example.code:
static GlsEmMcu myGlsEmMcu;

   public ControlSystemGlsEmMcu()
   {
       //Register console commands
       CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a GLS-EM-MCU CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerIdentifierCmd, "TriggerIdentifier", "This is a GLS-EM-MCU TriggerIdentifier command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(DeviceIdentifierCmd, "DeviceIdentifier", "This is a GLS-EM-MCU DeviceIdentifier command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerPhaseVoltageCmd, "TriggerPhaseVoltage", "This is a GLS-EM-MCU TriggerPhaseVoltage command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerPhaseActivePowerCmd, "TriggerPhActivePwr", "This is a GLS-EM-MCU TriggerPhActivePwr command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerActivePowerTotalCmd, "TriggerActivePwrTtl", "This is a GLS-EM-MCU TriggerActivePwrTtl command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerPulseRateCmd, "TriggerPulseRate", "This is a GLS-EM-MCU TriggerPulseRate command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintNumberOfRecordsCmd, "PrintNumberOfRecords", "This is a GLS-EM-MCU PrintNumberOfRecords command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintDataVoltageCmd, "PrintDataVoltage", "This is a GLS-EM-MCU PrintDataVoltage command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintDataCurrentCmd, "PrintDataCurrent", "This is a GLS-EM-MCU PrintDataCurrent command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintDataActivePowerCmd, "PrintDataActivePower", "This is a GLS-EM-MCU PrintDataActivePower command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintDataPeakPowerCmd, "PrintDataPeakPower", "This is a GLS-EM-MCU PrintDataPeakPower command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintEnergyUsageCmd, "PrintEnergyUsage", "This is a GLS-EM-MCU PrintEnergyUsage command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintPulseUsageCmd, "PrintPulseUsage", "This is a GLS-EM-MCU GetPulseUsage command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintPowerFactorCmd, "PrintPowerFactor", "This is a GLS-EM-MCU PrintPowerFactor command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintEnergyMeterCmd, "PrintEnergyMeter", "This is a GLS-EM-MCU PrintEnergyMeter command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintPulseMeterCmd, "PrintPulseMeter", "This is a GLS-EM-MCU PrintPulseMeter command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(GetPreviousRecordCmd, "GetPreviousRecord", "This is a GLS-EM-MCU GetPreviousRecord command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(GetNextRecordCmd, "GetNextRecord", "This is a GLS-EM-MCU GetNextRecord command", ConsoleAccessLevelEnum.AccessOperator);

       CrestronConsole.AddNewConsoleCommand(TriggerAllRealtimeDataCmd, "TriggerAllRtData", "This is a GLS-EM-MCU TriggerAllRtData command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(SetLiveUpdateRateCmd, "SetLiveUpdateRate", "This is a GLS-EM-MCU SetLiveUpdateRate command", ConsoleAccessLevelEnum.AccessOperator);

       CrestronConsole.AddNewConsoleCommand(PrintPhaseVoltageCmd, "PrintPhaseVoltage", "This is a GLS-EM-MCU PrintPhaseVoltage command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintPhaseActivePowerCmd, "PrintPhActivePower", "This is a GLS-EM-MCU PrintPhaseActivePower command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintActivePowerTotalCmd, "PrintActivePwrTotal", "This is a GLS-EM-MCU PrintActivePowerTotal command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintPulseRateCmd, "PrintPulseRate", "This is a GLS-EM-MCU PrintPulseRate command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerAllRealTimePowerCmd, "TriggerAllRealTimePower", "This is a GLS-EM-MCU TriggerAllRealTimePower command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(TriggerRealTimePowerCmd, "TriggerRealTimePower", "This is a GLS-EM-MCU TriggerRealTimePower command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(PrintRealTimePowerCmd, "PrintRealTimePower", "This is a GLS-EM-MCU PrintRealTimePower command", ConsoleAccessLevelEnum.AccessOperator);
   }

   //Method to create and register the GLS-EM-MCU device with the Control System.
   public void CreateDeviceCmd(String args)
   {
       //Initialize the GLS-EM-MCU device for Cresnet Communication
       myGlsEmMcu = new GlsEmMcu(0x18, this);

       myGlsEmMcu.BaseEvent += GlsEmMcu_EventHandler;
       for (uint i = 1; i <= 4; i++)
       {
           myGlsEmMcu.Branches[i].BranchStateChange += GlsEmMcu_CircuitSigEventHandler;
       }

       myGlsEmMcu.MasterRealTime.PhaseStateChange += GlsEmMcu_PhaseSigEventHandler;


       //Register the GLS-EM-MCU with the Control system
       myGlsEmMcu.Register();

       if (myGlsEmMcu.Registered)
       {
           CrestronConsole.ConsoleCommandResponse("{0} device successfully registered\r\n", myGlsEmMcu.ToString());
       }
   }

   //Method to handle sig change events for GLS-EM-MCU Phase class.
   static void GlsEmMcu_PhaseSigEventHandler(object device, PhaseEventArgs args)
   {
       if (args.EventId == GlsEmMcu.PhaseVoltageFeedbackEventId)
       {
           CrestronConsole.PrintLine("Phase {0} voltage feedback event received\r\n", Convert.ToChar(args.Index + 'A' -1));
       }
       else if (args.EventId == GlsEmMcu.PhaseActivePowerFeedbackEventId)
       {
           CrestronConsole.PrintLine("Phase {0} active power feedback event received\r\n", Convert.ToChar(args.Index + 'A' - 1));
       }
   }

   // Method to handle sig change events for GLS-EM-MCU Circuit class.
   static void GlsEmMcu_CircuitSigEventHandler(object device, BranchEventArgs args)
   {
       if (args.EventId == GlsEmMcu.RealTimePowerFeedbackEventId)
       {
           CrestronConsole.PrintLine("Branch {0} circuit {1} real-time active power feedback event received\r\n",
               args.Branch.Number, args.Index);
       }
   }

   // Method to handle sig change events for GLS-EM-MCU LoggingData, DeviceIdentifier and MasterRealTimeData classes.
   static void GlsEmMcu_EventHandler(GenericBase device, BaseEventArgs args)
   {
       switch (args.EventId)
       {
           case GlsEmMcu.PulseRateFeedbackEventId:
               CrestronConsole.PrintLine("Pulse Rate {0} feedback Event received\r\n", args.Index);
               break;
           case GlsEmMcu.ActivePowerTotalFeedbackEventId:
               CrestronConsole.PrintLine("Active Power Total feedback Event received\r\n");
               break;
           case GlsEmMcu.NumberOfRecordsFeedbackEventId:
               CrestronConsole.PrintLine("Number Of Log Records feedback Event received\r\n");
               break;
           case GlsEmMcu.DataActivePowerFeedbackEventId:
               CrestronConsole.PrintLine("Data Active Power feedback Event received\r\n");
               break;
           case GlsEmMcu.DataCurrentFeedbackEventId:
               CrestronConsole.PrintLine("Data Current feedback Event received\r\n");
               break;
           case GlsEmMcu.DataEnergyMeterFeedbackEventId:
               CrestronConsole.PrintLine("Data Energy Meter feedback Event received\r\n");
               break;
           case GlsEmMcu.DataEnergyUsageFeedbackEventId:
               CrestronConsole.PrintLine("Data Energy Usage feedback Event received\r\n");
               break;
           case GlsEmMcu.DataPeakPowerFeedbackEventId:
               CrestronConsole.PrintLine("Data Peak Power feedback Event received\r\n");
               break;
           case GlsEmMcu.DataPowerFactorFeedbackEventId:
               CrestronConsole.PrintLine("Data Power Factor feedback Event received\r\n");
               break;
           case GlsEmMcu.DataPulseMeterFeedbackEventId:
               CrestronConsole.PrintLine("Data Pulse Meter feedback Event received\r\n");
               break;
           case GlsEmMcu.DataPulseUsageFeedbackEventId:
               CrestronConsole.PrintLine("Data Pulse Usage feedback Event received\r\n");
               break;
           case GlsEmMcu.DataVoltageFeedbackEventId:
               CrestronConsole.PrintLine("Data Voltage feedback Event received\r\n");
               break;
           case GlsEmMcu.DeviceIdentifierFeedbackEventId:
               CrestronConsole.PrintLine("Device Identifier feedback Event received\r\n");
               break;
       }
   }

   //Prints the device identifier string
   //How to call this method: DeviceIdentifier
   public void DeviceIdentifierCmd(String args)
   {
       CrestronConsole.ConsoleCommandResponse("{0} Device Identifier is {1}\r\n", myGlsEmMcu.ToString(), myGlsEmMcu.DeviceIdentifierFeedback.StringValue);
   }

   //Method to trigger the myGlsEmMcu.DeviceIdentifierFeedback to report the name of the device.
   //How to call this method: TriggerIdentifier
   public void TriggerIdentifierCmd(String args)
   {
       myGlsEmMcu.TriggerIdentifier();
   }

   //Method to trigger to report current value of the myGlsEmMcu.MasterRealTime.PhaseVoltage for the specified phase.
   //How to call this method:  TriggerPhaseVoltage A/B/C Y to enable reporting
   //                          TriggerPhaseVoltage A/B/C N to enable reporting
   public void TriggerPhaseVoltageCmd(String args)
   {
       var ch = Convert.ToChar(args.Substring(0, 1));

       if ((ch >= 'A') && (ch <= 'C'))
       {
           var index = (uint)(ch - 'A');
           if (args.Substring(2, 1) == "Y")
           {
               myGlsEmMcu.MasterRealTime.Phases[index].TriggerVoltage.BoolValue = true;
           }
           else
           {
               myGlsEmMcu.MasterRealTime.Phases[index].TriggerVoltage.BoolValue = false;
           }
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Phase {0} is not valid\r\n", ch);
       }
   }

   //Method to trigger to report current value of the myGlsEmMcu.MasterRealTime.PhaseActivePower for the specified phase.
   //How to call this method:  TriggerPhActivePwr A/B/C Y to enable reporting
   //                          TriggerPhActivePwr A/B/C N to enable reporting
   public void TriggerPhaseActivePowerCmd(String args)
   {
       var ch = Convert.ToChar(args.Substring(0, 1));

       if ((ch >= 'A') && (ch <= 'C'))
       {
           var index = (uint)(ch - 'A');
           if (args.Substring(2, 1) == "Y")
           {
               myGlsEmMcu.MasterRealTime.Phases[index].TriggerActivePower.BoolValue
                   = true;
           }
           else
           {
               myGlsEmMcu.MasterRealTime.Phases[index].TriggerActivePower.BoolValue
                   = false;
           }
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Phase {0} is not valid\r\n", ch);
       }
   }

   //Method to trigger to report current value of the myGlsEmMcu.MasterRealTime.TriggerActivePowerTotal for the specified phase.
   //How to call this method:  TriggerActivePwrTtl Y to enable reporting
   //                          TriggerActivePwrTtl N to enable reporting
   public void TriggerActivePowerTotalCmd(String args)
   {
       if (args.Substring(0, 1) == "Y")
       {
           myGlsEmMcu.MasterRealTime.TriggerActivePowerTotal.BoolValue = true;
       }
       else
       {
           myGlsEmMcu.MasterRealTime.TriggerActivePowerTotal.BoolValue = false;
       }
   }

   //Method to trigger to report current value of the myGlsEmMcu.MasterRealTime.TriggerPulseRates for the specified pulse rate.
   //How to call this method:  TriggerPulseRate 1 to 4 Y to enable reporting
   //                          TriggerPulseRate 1 to 4 N to enable reporting
   public void TriggerPulseRateCmd(String args)
   {
       if (args.Substring(2, 1) == "Y")
       {
           myGlsEmMcu.MasterRealTime.TriggerPulseRates[Convert.ToUInt16(args.Substring(0, 1))].BoolValue = true;
       }
       else
       {
           myGlsEmMcu.MasterRealTime.TriggerPulseRates[Convert.ToUInt16(args.Substring(0, 1))].BoolValue = false;
       }
   }

   //Method to print the current value of the number of records logged.
   //How to call this method: PrintNumberOfRecords
