   }

   //Method to print the average voltage for each of the 3 input phases from the selected record.
   //How to call this method: PrintDataVoltage
   public void PrintDataVoltageCmd(String args)
   {
       CrestronConsole.ConsoleCommandResponse("{0} device's average voltage data in the selected record is {1}\r\n", myGlsEmMcu.ToString(), myGlsEmMcu.DataLogger.DataVoltageFeedback.StringValue);
       CrestronConsole.ConsoleCommandResponse("Packet Structure: <end time><duration><packet type><Avg_Voltage A><Avg_Voltage B><Avg_Voltage C>\r\n");
   }

   //Method to print the average RMS current measured for each channel, including a summary of all current measured through the 3 mains inputs from the selected record.
   //How to call this method: PrintDataCurrent
   public void PrintDataCurrentCmd(String args)
   {
       CrestronConsole.ConsoleCommandResponse("{0} device's average current data in the selected record is {1}\r\n", myGlsEmMcu.ToString(), myGlsEmMcu.DataLogger.DataCurrentFeedback.StringValue);
       CrestronConsole.ConsoleCommandResponse("Packet Structure: <end time><duration><packet type><Avg_Current A+B+C><Avg_Current A><Avg_Current B><Avg_Current C><Avg_Current Branch 1>…<Avg_Current Branch 84>");
   }

