       myGlsEmMcu.DataLogger.GetPrevious();
   }

   //Method to trigger the BranchCircuit class properties to report the corresponding real-time power values.
   //How to call this method: TriggerAllRtData Y to enable reporting
   //                         TriggerAllRtData N to enable reporting
   public void TriggerAllRealtimeDataCmd(String args)
   {
       if (args.Substring(0, 1) == "Y")
       {
           myGlsEmMcu.MasterRealTime.TriggerAllRealTimeData.BoolValue = true;
       }
       else
       {
           myGlsEmMcu.MasterRealTime.TriggerAllRealTimeData.BoolValue = true;
       }
   }

   //Method to set the live update rate.
   //How to call this method: SetLiveUpdateRate XXXXX(decimal value maximum: 65535)
   public void SetLiveUpdateRateCmd(String args)
   {
       myGlsEmMcu.MasterRealTime.LiveUpdateRate.UShortValue = Convert.ToUInt16(args.Substring(0, 5));
   }

   //Method to print the specified phase real-time voltage value.
   //How to call this method: PrintPhaseVoltage A/B/C
   public void PrintPhaseVoltageCmd(String args)
   {
       var phase = Convert.ToChar(args.Substring(0, 1));

       if ((phase >= 'A') && (phase <= 'C'))
       {
           var i = (ushort) (phase - 'A' + 1);
           CrestronConsole.ConsoleCommandResponse("Phase {0} real-time voltage value is {1}\r\n", phase, myGlsEmMcu.MasterRealTime.Phases[i].VoltageFeedback.UShortValue);
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Phase {0} is not valid\r\n", phase);
       }
   }

   //Method to print the specified phase real-time active power value.
   //How to call this method: PrintPhActivePower A/B/C
   public void PrintPhaseActivePowerCmd(String args)
   {
       var phase = Convert.ToChar(args.Substring(0, 1));

       if ((phase >= 'A') && (phase <= 'C'))
       {
           var i = (ushort)(phase - 'A' + 1);
           CrestronConsole.ConsoleCommandResponse("Phase {0} active power value is {1}\r\n", phase, myGlsEmMcu.MasterRealTime.Phases[i].ActivePowerFeedback.UShortValue);
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Phase {0} is not valid\r\n", phase);
       }
   }

   //Method to print the total of real-time active power values of the three phases.
   //How to call this method: PrintActivePwrTotal
   public void PrintActivePowerTotalCmd(String args)
   {
       CrestronConsole.ConsoleCommandResponse("Total active power value is {0}\r\n", myGlsEmMcu.MasterRealTime.ActivePowerTotalFeedback.UShortValue);
   }

   //Method to print the specified pulse rate value.
   //How to call this method: PrintPulseRate 1 to 4
   public void PrintPulseRateCmd(String args)
   {
       var pulsenumber = Convert.ToUInt16(args.Substring(0, 1));

       if ((pulsenumber >= 1) && (pulsenumber <= 4))
       {
           CrestronConsole.ConsoleCommandResponse("Pulse {0} rate value is {1}\r\n", pulsenumber, myGlsEmMcu.MasterRealTime.PulseRateFeedbacks[pulsenumber].UShortValue);
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Pulse {0} is not valid\r\n", pulsenumber);
       }
   }

   //Method to trigger the reporting of all the real-time power data.
   //How to call this method: TriggerAllRealTimePower 1 to 4 Y enable reporting
   //                         TriggerAllRealTimePower 1 to 4 N disable reporting
   public void TriggerAllRealTimePowerCmd(String args)
   {
       var br = Convert.ToUInt16(args.Substring(0, 1));
       if (br <= 4)
       {
           if ((args.Substring(2, 1) == "Y"))
           {
               myGlsEmMcu.Branches[br].TriggerAllRealTimePowerData.BoolValue = true;
           }
           else
           {
               myGlsEmMcu.Branches[br].TriggerAllRealTimePowerData.BoolValue = false;
           }
       }
   }

   //Method to trigger the reporting of the specified real-time power of the specified branch.
   //How to call this method: TriggerRealTimePower 1 to 4 01 to 21 Y enable reporting
   //                         TriggerRealTimePower 1 to 4 01 to 21 N disable reporting
   public void TriggerRealTimePowerCmd(String args)
   {
       var br = Convert.ToUInt16(args.Substring(0, 1));
       var i = Convert.ToUInt16(args.Substring(2, 2));

       if (br <= 4)
       {
           if (i <= 21)
           {
               if ((args.Substring(5, 1) == "Y"))
               {
                   myGlsEmMcu.Branches[br].Circuits[i].TriggerRealTimePowerData.BoolValue = true;
               }
               else
               {
                   myGlsEmMcu.Branches[br].Circuits[i].TriggerRealTimePowerData.BoolValue = false;
               }
           }
           else
           {
               CrestronConsole.ConsoleCommandResponse("Index {0} into the RealTimePower collection is not valid\r\n", i);
           }
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Branch number {0} is not valid\r\n", br);
       }
   }

   //Method to print the specified real-time power value of the specified branch.
   //How to call this method: PrintRealTimePower 1 to 4 01 to 21
   public void PrintRealTimePowerCmd(String args)
   {
       var br = Convert.ToUInt16(args.Substring(0, 1));
       var i = Convert.ToUInt16(args.Substring(2, 2));

       if (br <= 4)
       {
           if (i <= 21)
           {
               CrestronConsole.ConsoleCommandResponse("RealTimePower {0} value is {1}\r\n", i, myGlsEmMcu.Branches[br].Circuits[i].RealTimePowerFeedback.UShortValue);
           }
           else
           {
               CrestronConsole.ConsoleCommandResponse("Index {0} into the RealTimePower collection is not valid\r\n", i);
           }
       }
       else
       {
           CrestronConsole.ConsoleCommandResponse("Branch number {0} is not valid\r\n", br);
       }
   }

static Clx1Mc4 clx1Mc4;

   public ControlSystemClx1Mc4()
   {
       //Register console commands
       CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a CLX-1MC4 Motor Control Module CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(SetMotorStateCmd, "SetMotorState", "This is a CLX-1MC4 Motor Control Module SetMotorState command", ConsoleAccessLevelEnum.AccessOperator);
       CrestronConsole.AddNewConsoleCommand(SetMaximumTimeCmd, "SetMaximumTime", "This is a CLX-1MC4 Motor Control Module SetMaximumTime command", ConsoleAccessLevelEnum.AccessOperator);
   }

   // Method to handle sig change events for CLX-1MC4 Motor Control Module
   static void Clx1Mc4_EventHandler(GenericDevice device, BaseEventArgs args)
   {
       if (args.EventId == ClxEventIds.ErrorFeedbackEventId)
       {
           CrestronConsole.PrintLine("Error Feedback Event Received");
       }
       else if (args.EventId == ClxEventIds.InOverrideFeedbackEventId)
       {
           CrestronConsole.PrintLine("InOverride Feedback Event Received");
       }
   }

   //Method to create the CLX-1MC4 device
   //How to use this command:  CreateDevice
   public void CreateDeviceCmd(String args)
   {
       //Initialize the CLX-1MC4 device for Cresnet Communication
       clx1Mc4 = new Clx1Mc4(0x17, this);
       CrestronConsole.ConsoleCommandResponse("CLX-1MC4 device successfully created with CresnetID : {0}", clx1Mc4.ID);

       clx1Mc4.BaseEvent += Clx1Mc4_EventHandler;

       //Register the CLX-1MC4 Cresnet Motor Control device with the Control system
       clx1Mc4.Register();
   }

   //Method to set the state of the motor the CLX-1MC4 Motor Control Module controls
   //How to use the command:   SetMotorState 1/2 (Motor Number) 0-9 (Motor State)
   public void SetMotorStateCmd(String args)
   {
       uint state = Convert.ToUInt16(args.Substring(2, 1));
       uint motorNumber = Convert.ToUInt16(args.Substring(0, 1));

       clx1Mc4.Motors[motorNumber].MotorState = (eClxMotorState)state;
       CrestronConsole.PrintLine("Motor {0} state is set to {1}", motorNumber, clx1Mc4.Motors[motorNumber].MotorState);
   }

   //Method to set the maximum time to move the specified motor
   //How to use the command:   SetMaximumTime  (Motor Number - 1/2) (Value - in range from 00000 to 65535)
   public void SetMaximumTimeCmd(String args)
   {
       var motorNumber = Convert.ToUInt16(args.Substring(0, 1));

       clx1Mc4.Motors[motorNumber].ParameterMaximumTime = Convert.ToUInt16(args.Substring(2, 5));
       CrestronConsole.ConsoleCommandResponse("Motor {0} Maximum Time is set to {1}\r\n", motorNumber, clx1Mc4.Motors[motorNumber].ParameterMaximumTime);
   }

