// Method to handle sig change events for GLX-HSW8 device.
static void GlxHsw8EventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == GlxxEventId.NoAcPowerFeedbackEventId)
    {
        CrestronConsole.PrintLine("No AC Power Feedback Event Occurred");
    }
}

//Event handler for Override changes.
static void GlxxOverrideEventHandler(GenericBase device, eOverrideEvents overrideEvent)
{
    if (overrideEvent == eOverrideEvents.External)
    {
        CrestronConsole.PrintLine("{0} device External Override event Occurred", _glxHsw8.ToString());
    }
}

//Event handler for load state changes.
static void GlxLoadStateChangeEventHandler(LightingBase lightingObject, LoadEventArgs args)
{
    if (args.EventId == LoadEventIds.IsOnEventId)
    {
        CrestronConsole.PrintLine("{0} device Load On event Occurred", _glxHsw8.ToString());
    }
}

