member.example:
Example program using the Wireless Horizon Switch. 


using Crestron.SimplSharp;
using Crestron.SimplSharpPro;
using Crestron.SimplSharpPro.DeviceSupport;
using Crestron.SimplSharpPro.Gateways;
using Crestron.SimplSharpPro.Keypads;
using Crestron.SimplSharpPro.Lighting.Horizon;

namespace HzSwExTest
{
    public class ControlSystem : CrestronControlSystem
    {
        private HzSwEx _exHorizonSwitch;
        private CenRfgwEx _rfGateway;

        //Program constructor
        public ControlSystem()
            : base()
        {

            //Setup some console commands
            CrestronConsole.AddNewConsoleCommand(CreateDevice, "CreateDevice", "Create the Horizon Ex Switch for this program.", ConsoleAccessLevelEnum.AccessOperator);
            CrestronConsole.AddNewConsoleCommand(CycleFeedbacks, "CycleFeedbacks", "Fucntion to cycle the state of feedbacks on the Ex Switch.", ConsoleAccessLevelEnum.AccessOperator);
            CrestronConsole.AddNewConsoleCommand(CycleDayTimeCmd, "CycleDayTime", "Cycle the Ex Switch between Day/Night time modes.", ConsoleAccessLevelEnum.AccessProgrammer);
            CrestronConsole.AddNewConsoleCommand(CycleLoadsCmd, "CycleLoads", "Cycle the Switch loads of this Ex Switch.", ConsoleAccessLevelEnum.AccessProgrammer);

        }

        //Command handler to call a function the change the state of the 
        private void CycleLoadsCmd(string cmdParameters)
        {
            //Cycle all the loads between on/off
            CycleSwitchLoads(_exHorizonSwitch);
        }

        //function will cycle the switched loads of the device passed in.
        private void CycleSwitchLoads(ISwitch switchDevice)
        {
            //Let's toggle all of the switch loads on this device.
            foreach (var swLoad in switchDevice.SwitchedLoads)
            {
                if (swLoad.IsOn)
                {
                    swLoad.FullOff();
                    CrestronConsole.ConsoleCommandResponse("Turning off Load {0} from device {1}.\r\n", swLoad.Number, swLoad.DeviceLoadIsOn);
                }
                else
                {
                    swLoad.FullOn();
                    CrestronConsole.ConsoleCommandResponse("Turning on Load {0} from device {1}.\r\n", swLoad.Number, swLoad.DeviceLoadIsOn);
                }
            }
        }

        //function to cycle between Day and Night time modes for the LEDs of the keypad.
        private void CycleDayTimeCmd(string cmdParameters)
        { 
            if (_exHorizonSwitch == null)
            {
                CrestronConsole.ConsoleCommandResponse("EX Switch is not initialized.\r\n");
                return;
            }

            if (_exHorizonSwitch.BacklightLedSettings.InNightTimeFeedback.BoolValue)
            {
                _exHorizonSwitch.BacklightLedSettings.EnableDayTime();
                CrestronConsole.ConsoleCommandResponse("Enable Daytime mode of the switch's keypad part.\r\n");

            }
            else
            {
                _exHorizonSwitch.BacklightLedSettings.EnableNightTime();
                CrestronConsole.ConsoleCommandResponse("Enable Nighttime mode of the switch's keypad.\r\n");
            }
        }

        //Function to cycle the current state of the button's feedbacks. 
        private void CycleFeedbacks(string cmdParameters)
        {
            if (_exHorizonSwitch == null)
            {
                CrestronConsole.ConsoleCommandResponse("EX Switch is not initialized.\r\n");
                return;
            }

            PrintAndToggleFeedbacks(_exHorizonSwitch.Feedbacks);

        }

        private void PrintAndToggleFeedbacks<T>(CrestronCollection<T> feedbacks) where T : Feedback
        {
            //Let's start with printing all the feedbacks
            for (uint i = 0, found = 0; found < feedbacks.Count; i++)
            {
                T feedback;
                //Try get the current index
                if (feedbacks.TryGetValue(i, out feedback))
                {
                    //Success let's try the indexer 
                    var localFeedback = feedbacks[i];
                    //Toggle the state of the feedback
                    localFeedback.State = !localFeedback.State;

                    CrestronConsole.ConsoleCommandResponse("Feedback[{0}]: {1}\r\n", i, localFeedback.State);

                    //Let's see if this is an active feedback
                    var activeFeedback = localFeedback as FeedbackWithActive;
                    if (activeFeedback != null)
                    {
                        activeFeedback.Active.BoolValue = !activeFeedback.Active.BoolValue;

                        CrestronConsole.ConsoleCommandResponse("Feedback[{0}]: Active State: {1}\r\n", i, activeFeedback.Active.BoolValue);
                    }

                    found++;
                }

                if (i > 20)
                {//If we somehow got to an index of 20 let's break out ...
                    break;
                }
            }
        }

        private void CreateDevice(string cmdParameters)
        {
            if (_rfGateway != null)
            {//This means we've already create the device... I hope. 
                CrestronConsole.ConsoleCommandResponse("Device already created.");
            }

            _rfGateway = new CenRfgwEx(0x44, this);
            _exHorizonSwitch = new HzSwEx(0x30, _rfGateway);

            //Register the event handlers for the switch
            _exHorizonSwitch.ButtonStateChange += ButtonPressHandler;
            _exHorizonSwitch.BaseEvent += ExHorizonSwitchOnBaseEvent;
            _exHorizonSwitch.OnlineStatusChange += ExHorizonSwitchOnOnlineStatusChange;
            _exHorizonSwitch.LoadStateChange += ExHorizonSwitchOnLoadStateChange;
            _rfGateway.Register();
        }

        private void ExHorizonSwitchOnLoadStateChange(LightingBase lightingObject, LoadEventArgs args)
        {
            var switchLoad = args.Load as SwitchedLoad;

            if (switchLoad == null)
                return; //Odd

            CrestronConsole.PrintLine("Switched Load[{0}] is now {1}", switchLoad.Number, switchLoad.IsOn ? "On" : "Off");
        }

        private void ButtonPressHandler(GenericBase device, ButtonEventArgs args)
        {
            CrestronConsole.PrintLine("Button[{0}]'s state is: {1}", args.Button.Name, args.Button.State);
        }

        private void ExHorizonSwitchOnOnlineStatusChange(GenericBase currentDevice, OnlineOfflineEventArgs args)
        {
            //Print a message when the online state of the keypad changes.
            if (currentDevice == _exHorizonSwitch)
            {
                CrestronConsole.PrintLine("The Horizon Ex Switch is now : {0}", args.DeviceOnLine ? "Online" : "Offline");
            }
        }

        private void ExHorizonSwitchOnBaseEvent(GenericBase device, BaseEventArgs args)
        {
            //Need to check for some specific error cases..
            switch (args.EventId)
            {
                case (HzKpBase.LocalColorThemeFeedbackEventId):
                    CrestronConsole.PrintLine("The Switch's Local Color Theme has changed to : {0}", _exHorizonSwitch.BacklightLedSettings.LocalColorThemeFeedback);
                    break;
                case (HzKpBase.InDayTimeEventId):
                    if (_exHorizonSwitch.BacklightLedSettings.InDayTimeFeedback.BoolValue)
                        CrestronConsole.PrintLine("The Switch is now in Day time mode.");
                    break;
                case (HzKpBase.InNightTimeEventId):
                    if (_exHorizonSwitch.BacklightLedSettings.InNightTimeFeedback.BoolValue)
                        CrestronConsole.PrintLine("The Switch is now in Night time mode.");
                    break;
            }
        }
	}
}

