   //Method to handle the Motor Control Slot events
   public void ClciMcexBaseEvent(GenericBase device, BaseEventArgs args)
   {
       if (args.EventId == Din2Mc2.IsMovingEventId)
       {
           CrestronConsole.PrintLine("Is Moving event occurred, Value is {0}.\r\n", _myClciMcex.MotorControl.IsMovingFeedback.BoolValue);
       }
       else if (args.EventId == Din2Mc2.LastDirectionIsOpenEventId)
       {
           CrestronConsole.PrintLine("Last Direction Is Open event occurred, Value is {0}.\r\n", _myClciMcex.MotorControl.IsLastDirectionOpenFeedback.BoolValue);
       }
       else if (args.EventId == Din2Mc2.LastDirectionIsCloseEventId)
       {
           CrestronConsole.PrintLine("Last Direction Is Close event occurred, Value is {0}.\r\n", _myClciMcex.MotorControl.IsLastDirectionCloseFeedback.BoolValue);
       }
   }

// Register the device within the constructor or InitializeSystem function
public ControlSystem() : base()
{
	myDinAO8 = new DinAo8(0x89, this);
	// Now register the event handlers
	myDinAO8.OverrideEventHandler += new OverrideHandler(OverrideEventHandler);
	myDinAO8.OnlineStatusChange += new OnlineStatusChangeEventHandler(OnlineStatusChangeCallback);
	myDinAO8.BaseEvent += new BaseEventHandler(BaseEventCallback);

	// Need to set parameters before we register the device
	foreach (DinAnalogOutput analogOutput in myDinAO8.DinLoads)
	{
		analogOutput.ParameterOverrideLevel = SimplSharpDeviceHelper.PercentToUshort(50);
	}
	// Register the device now
	if (myDinAO8.Register() != eDeviceRegistrationUnRegistrationResponse.Success)
	{
		CrestronConsole.PrintLine(" Unable to register for device ");
	}
	else
	{
		CrestronConsole.PrintLine(" Device successfully registered ");
	}
}

// Online Offline Status Change Event Handler 
void OnlineStatusChangeCallback(GenericBase currentDevice, OnlineOfflineEventArgs args)
{
	CrestronConsole.PrintLine(" Din AO8 state {0} ", args.DeviceOnLine ? "Online" : "Offline");
}

// Base Event handler for the DinAo8
void BaseEventCallback(GenericBase device, BaseEventArgs args)
{
	switch (args.EventId)
	{
		case DinLoadBaseClass.LevelOutFeedbackEventId:
			CrestronConsole.PrintLine(" Level out changed for output {0} ", args.Index);
			CrestronConsole.PrintLine(" Value of load is {0} ", myDinAO8.AnalogOutputs[(uint)args.Index].LevelOut.UShortValue);
			break;
	}
}

// Override handler for the DinAo8
void OverrideEventHandler(GenericBase device, eOverrideEvents overrideEvent)
{
	switch (overrideEvent)
	{
		case eOverrideEvents.External:
			CrestronConsole.PrintLine(" Device  reports external override event. State is {0} ", myDinAO8.InExternalOverrideFeedback.BoolValue);
			break;
		case eOverrideEvents.Manual:
			CrestronConsole.PrintLine(" Device  reports manual override event. State is {0} ", myDinAO8.InManualOverrideFeedback.BoolValue);
			break;
	}
}

member.example:
Example program using the Wireless Horizon Dimmer. 


using Crestron.SimplSharp;                          	
using Crestron.SimplSharpPro;                       	
using Crestron.SimplSharpPro.DeviceSupport; 
using Crestron.SimplSharpPro.Gateways;
using Crestron.SimplSharpPro.Keypads;
using Crestron.SimplSharpPro.Lighting.Horizon;

namespace HzDimExTest
{
    public class ControlSystem : CrestronControlSystem
    {
        private HzDimEx _exHorizonDimmer;
        private CenRfgwEx _rfGateway;

        //Program constructor
        public ControlSystem()
            : base()
        {

            //Setup some console commands
            CrestronConsole.AddNewConsoleCommand(CreateDevice, "CreateDevice", "Create the Horizon Ex Switch for this program.", ConsoleAccessLevelEnum.AccessOperator);
            CrestronConsole.AddNewConsoleCommand(CycleFeedbacks, "CycleFeedbacks", "Fucntion to cycle the state of feedbacks on the Ex Switch.", ConsoleAccessLevelEnum.AccessOperator);
            CrestronConsole.AddNewConsoleCommand(CycleDayTimeCmd, "CycleDayTime", "Cycle the Ex Switch between Day/Night time modes.", ConsoleAccessLevelEnum.AccessProgrammer);
            CrestronConsole.AddNewConsoleCommand(CycleLoadsCmd, "CycleLoads", "Cycle the Dimming loads of this Ex Dimmer.", ConsoleAccessLevelEnum.AccessProgrammer);

        }

        //Command handler to call a function the change the state of the 
        private void CycleLoadsCmd(string cmdParameters)
        {
            //Cycle all the loads between on/off
            CycleDimmingLoads(_exHorizonDimmer);
        }

        //function will cycle the switched loads of the device passed in.
        private void CycleDimmingLoads(IDimmer dimmingDevice)
        {
            //Let's toggle all of the loads on this device.
            foreach (var dimLoad in dimmingDevice.DimmingLoads)
            {
                //If the load is on
                if (dimLoad.LevelFeedback.UShortValue > 0)
                {
                    dimLoad.FullOff();
                    CrestronConsole.ConsoleCommandResponse("Turning off Load {0} from device {1}.\r\n", dimLoad.Number, dimLoad.Parent);
                }
                else
                {
                    dimLoad.FullOn();
                    CrestronConsole.ConsoleCommandResponse("Turning on Load {0} from device {1}.\r\n", dimLoad.Number, dimLoad.Parent);
                }
            }
        }

        //function to cycle between Day and Night time modes for the LEDs of the keypad.
        private void CycleDayTimeCmd(string cmdParameters)
        {
            if (_exHorizonDimmer == null)
            {
                CrestronConsole.ConsoleCommandResponse("EX Switch is not initialized.\r\n");
                return;
            }

            if (_exHorizonDimmer.BacklightLedSettings.InNightTimeFeedback.BoolValue)
            {
                _exHorizonDimmer.BacklightLedSettings.EnableDayTime();
                CrestronConsole.ConsoleCommandResponse("Enable Daytime mode of the switch's keypad part.\r\n");

            }
            else
            {
                _exHorizonDimmer.BacklightLedSettings.EnableNightTime();
                CrestronConsole.ConsoleCommandResponse("Enable Nighttime mode of the keypad.\r\n");
            }
        }

        //Function to cycle the current state of the button's feedbacks. 
        private void CycleFeedbacks(string cmdParameters)
        {
            if (_exHorizonDimmer == null)
            {
                CrestronConsole.ConsoleCommandResponse("EX Switch is not initialized.\r\n");
                return;
            }

            PrintAndToggleFeedbacks(_exHorizonDimmer.Feedbacks);

        }

        private void PrintAndToggleFeedbacks<T>(CrestronCollection<T> feedbacks) where T : Feedback
        {
            //Let's start with printing all the feedbacks
            for (uint i = 0, found = 0; found < feedbacks.Count; i++)
            {
                T feedback;
                //Try get the current index
                if (feedbacks.TryGetValue(i, out feedback))
                {
                    //Success let's try the indexer 
                    var localFeedback = feedbacks[i];
                    //Toggle the state of the feedback
                    localFeedback.State = !localFeedback.State;

                    CrestronConsole.ConsoleCommandResponse("Feedback[{0}]: {1}\r\n", i, localFeedback.State);

                    //Let's see if this is an active feedback
                    var activeFeedback = localFeedback as FeedbackWithActive;
                    if (activeFeedback != null)
                    {
                        activeFeedback.Active.BoolValue = !activeFeedback.Active.BoolValue;

                        CrestronConsole.ConsoleCommandResponse("Feedback[{0}]: Active State: {1}\r\n", i, activeFeedback.Active.BoolValue);
                    }

                    found++;
                }

                if (i > 20)
                {//If we somehow got to an index of 20 let's break out ...
                    break;
                }
            }
        }

        private void CreateDevice(string cmdParameters)
        {
            if (_rfGateway != null)
            {//This means we've already create the device... I hope. 
                CrestronConsole.ConsoleCommandResponse("Device already created.");
            }

            _rfGateway = new CenRfgwEx(0x44, this);
            _exHorizonDimmer = new HzDimEx(0x30, _rfGateway);

            //Register the event handlers for the dimmer
            _exHorizonDimmer.ButtonStateChange += ButtonPressHandler;
            _exHorizonDimmer.BaseEvent += ExHorizonDimmerOnBaseEvent;
            _exHorizonDimmer.OnlineStatusChange += ExHorizonSwitchOnOnlineStatusChange;
            _exHorizonDimmer.LoadStateChange += ExHorizonDimmerOnLoadStateChange;

            //Setup how this dimmer behaves 
            _exHorizonDimmer.ParameterLocalOnLevel = SimplSharpDeviceHelper.PercentToUshort(80); //Set the local on level to 80%
            _exHorizonDimmer.ParameterOffFadeTime = 100; //take one second to fade off.
            _exHorizonDimmer.ParameterOnFadeTime = 50; //Take half a second to fade on.
            _exHorizonDimmer.ParameterRaiseLowerRate = 100; //Take 1 second for level change operations.

            //Setup some dimmer parameters for the first load
            var firstLoad = _exHorizonDimmer.DimmingLoads[1];
            firstLoad.ParameterMaxLevel = SimplSharpDeviceHelper.PercentToUshort(80); //Set the max brightness to 80%
            firstLoad.ParameterMinLevel = SimplSharpDeviceHelper.PercentToUshort(5); //Set the min brightness to 5%


            _rfGateway.Register();
        }

        private void ExHorizonDimmerOnLoadStateChange(LightingBase lightingObject, LoadEventArgs args)
        {
            var dimmingLoad = args.Load as DimmingLoad;

            if (dimmingLoad == null)
                return; //Odd

            CrestronConsole.PrintLine("Dimming Load[{0}] is now {1}", dimmingLoad.Number, (dimmingLoad.LevelFeedback.UShortValue>0) ? "On" : "Off");
        }

        private void ButtonPressHandler(GenericBase device, ButtonEventArgs args)
        {
            CrestronConsole.PrintLine("Button[{0}]'s state is: {1}", args.Button.Name, args.Button.State);
        }

        private void ExHorizonSwitchOnOnlineStatusChange(GenericBase currentDevice, OnlineOfflineEventArgs args)
        {
            //Print a message when the online state of the keypad changes.
            if (currentDevice == _exHorizonDimmer)
            {
                CrestronConsole.PrintLine("The Horizon Ex Dimmer is now : {0}", args.DeviceOnLine ? "Online" : "Offline");
            }
        }

        private void ExHorizonDimmerOnBaseEvent(GenericBase device, BaseEventArgs args)
        {
            //Need to check for some specific error cases..
            switch (args.EventId)
            {
                case (HzKpBase.LocalColorThemeFeedbackEventId):
                    CrestronConsole.PrintLine("The Dimmer's Local Color Theme has changed to : {0}", _exHorizonDimmer.BacklightLedSettings.LocalColorThemeFeedback);
                    break;
                case (HzKpBase.InDayTimeEventId):
                    if (_exHorizonDimmer.BacklightLedSettings.InDayTimeFeedback.BoolValue)
                        CrestronConsole.PrintLine("The Dimmer is now in Day time mode.");
                    break;
                case (HzKpBase.InNightTimeEventId):
                    if (_exHorizonDimmer.BacklightLedSettings.InNightTimeFeedback.BoolValue)
                        CrestronConsole.PrintLine("The Dimmer is now in Night time mode.");
                    break;
            }
        }
    }

}

