}

// Base Event handler for the Din4Dimflv4
void BaseEventHandlerCallback(GenericBase device, BaseEventArgs args)
{
	switch (args.EventId)
	{
		case DinLoadBaseClass.LevelOutFeedbackEventId:
			CrestronConsole.PrintLine(" Level out changed for output {0} ", args.Index);
			CrestronConsole.PrintLine(" Value of load is {0} ", myDin4Dimflv4.AnalogOutputs[(uint)args.Index].LevelOut.UShortValue);
			break;
	}
}

// Override handler for the Din4Dimflv4
void OverrideEventHandler(GenericBase device, eOverrideEvents overrideEvent)
{
	switch (overrideEvent)
	{
		case eOverrideEvents.External:
			CrestronConsole.PrintLine(" Device  reports external override event. State is {0} ", myDin4Dimflv4.InExternalOverrideFeedback.BoolValue);
			break;
		case eOverrideEvents.Manual:
			CrestronConsole.PrintLine(" Device  reports manual override event. State is {0} ", myDin4Dimflv4.InManualOverrideFeedback.BoolValue);
			break;
	}
}

static ZummeshNetbridge zumMeshDevice;

public ControlSystemZummeshNetbridge()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a ZUMMESH-NETBRIDGE CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
    CrestronConsole.AddNewConsoleCommand(SetRoomNameCmd, "SetRoomName", "This is a ZUMMESH-NETBRIDGE SetRoomName command", ConsoleAccessLevelEnum.AccessOperator);
}

public void SetRoomNameCmd(String args)
{
    zumMeshDevice.RoomName.StringValue = "ConferenceRoom";
}

public void CreateDeviceCmd(String args)
{
    //For internal gateway
    zumMeshDevice = new ZummeshNetbridge(0x3, ControllerRFGatewayDevice);

    zumMeshDevice.BaseEvent += ZummeshNetbridgeEventHandler;

    //Register the ZUMMESH-NETBRIDGE with the control system
    zumMeshDevice.Register();
}

// Method to handle sig change events for ZUMMESH-NETBRIDGE Device.
static void ZummeshNetbridgeEventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == ZummeshNetbridge.RoomIsOnEventId)
    {
        CrestronConsole.PrintLine("{0} Room Is On Event Occurred", device.ToString());
    }
    else if (args.EventId == ZummeshNetbridge.SceneActiveEventId)
    {
        CrestronConsole.PrintLine("{0} Scene {1} Active Event Occurred", device.ToString(), args.Index);
    }
    else if (args.EventId == ZummeshNetbridge.MissingDevicesFeedbackEventId)
    {
        CrestronConsole.PrintLine("{0} Missing Devices Feedback Event Occurred", device.ToString());

        for (uint i = 1; i <= zumMeshDevice.MissingDevicesFeedback.Count; i++)
        {
            CrestronConsole.PrintLine("MissingDevicesFeedback Device Address is {0}, Name is {1}, TSID is {2}",
            zumMeshDevice.MissingDevicesFeedback[i].ShortAddress, zumMeshDevice.MissingDevicesFeedback[i].Name, zumMeshDevice.MissingDevicesFeedback[i].TSID);
        }
    }
}

static ClsC6 _clsC6x;

public ControlSystemClsC6x()
{
    //Register console commands
    CrestronConsole.AddNewConsoleCommand(CreateDeviceCmd, "CreateDevice", "This is a CLS-C6X CreateDevice command", ConsoleAccessLevelEnum.AccessOperator);
}

//Method to create CLS-C6/CLS-C6M device class object
public void CreateDeviceCmd(String args)
{
    //Initialize the CLS-C6 device for Cresnet Communication
    _clsC6x = new ClsC6(0x16, this);

    _clsC6x.BaseEvent += ClsC6_BaseEventHandler;
    _clsC6x.ButtonStateChange += ClsC6XButtonChange;
    _clsC6x.SceneControlChange += ClsC6SceneControlChange;
    _clsC6x.LoadStateChange += ClsC6LightingLoadStateChange;
    _clsC6x.SceneProgramming.SceneProgrammingStateChange += SceneProgrammingStateChange;
    _clsC6x.LoadSettingsChange += ClsC6LoadSettingsStateChange;

    _clsC6x.SceneProgramming.ProgrammedScene = _clsC6x.LastScene;

    //Register the CLS-C6 with the Control system
    //This will enable the gateway connection ("127.0.0.1") in the system at the 
    //ID specified above that the CLS-C6 will connect to.
    _clsC6x.Register();
}

// Method to handle Partition Control, Shade Groups, Daylight Harvesting, Occupancy Sensing, Combining sig change events for CLS-C6X 
static void ClsC6_BaseEventHandler(GenericBase device, BaseEventArgs args)
{
    if (args.EventId == ClsC6xBase.PhotocellControlEnabledForLoadEventId)
    {
        CrestronConsole.PrintLine("Photocell Control Enabled event for Load {0} received, state is {1}\r\n", args.Index, _clsC6x.DaylightHarvesting.LoadDaylightHarvesting[(uint)args.Index].EnabledFeedback.BoolValue);
    }
}

//Method to handle the button events
static void ClsC6XButtonChange(GenericBase currentDevice, ButtonEventArgs args)
{
    CrestronConsole.PrintLine("Button{0}'s state is {1}\r\n", args.Button.Number, args.Button.State);
}

//Method to handle the Light Override and Shed Mode slot sig events
static void ClsC6LoadSettingsStateChange(ClsC6xBase device, LoadSettingsEventArgs args)
{
    if (args.EventId == ClsC6xBase.ModeEnteredEventId)
    {
        CrestronConsole.PrintLine("Mode {0} Entered event occurred, EnteredFeedback value is {1}\r\n", args.LoadSettings.Mode, args.LoadSettings.EnteredFeedback.BoolValue);
    }
}

//Method to handle the Scene Programming slot sig change events
static void SceneProgrammingStateChange(ClsC6xBase paramDevice, SceneProgrammingLoadEventArgs args)
{
    if (args.EventId == ClsC6xBase.InSceneEventId)
    {
        CrestronConsole.PrintLine("In Scene event occurred for Scene Programming Load {0} Load Type {1}\r\n", args.ProgrammingLoad.Number, args.ProgrammingLoad.InSceneFeedback.BoolValue, args.ProgrammingLoad.LoadType);
    }
    else if (args.EventId == ClsC6xBase.InSetupModeEventId)
    {
        CrestronConsole.PrintLine("In setup mode event received, sig is {0}\r\n", _clsC6x.SceneProgramming.InSetupModeFeedback);
    }
    else if(args.EventId == ClsC6xBase.SceneNumberFeedbackEventId)
    {
       CrestronConsole.PrintLine("Scene Number Feedback event received, scene type is {0}\r\n", _clsC6x.SceneProgramming.ProgrammedSceneFeedback.Type);
    }
}

//Method to handle the Lighting Load slot sig change events
static void ClsC6LightingLoadStateChange(LightingBase lightingObject, LoadEventArgs args)
{
    if (args.EventId == LoadEventIds.LevelChangeEventId)
    {
        CrestronConsole.PrintLine("Level Changed event occurred for Load {0}, value is {1}\r\n", args.Load.Number, ((ClsC6xBase)lightingObject).LightingLoads[args.Load.Number].LevelFeedback);
    }
}

//Method to handle the Scene and Master Dimming slot sig change events
static void ClsC6SceneControlChange(ClsC6xBase paramDevice, SceneControlEventArgs args)
{
    if (args.EventId == ClsC6xBase.SceneRecallingEventId)
    {
        CrestronConsole.PrintLine("Scene Recalling event occurred for Scene {0} Scene Type {1}, value is {2}\r\n", args.SceneControl.Number, args.SceneControl.Type, ((ClsC6xBase.ClsC6Scene)args.SceneControl).RecallingFeedback.BoolValue);
    }
    else if (args.EventId == ClsC6xBase.SceneLightsUpEventId)
    {
        CrestronConsole.PrintLine("Scene Lights Up event occurred for Scene {0} Scene Type {1}, value is {2}\r\n", args.SceneControl.Number, args.SceneControl.Type, args.SceneControl.LightsUpFeedback.BoolValue);
    }
}

members.member:

Constructor used to add the device to the controller.

        /// 
ID of this device

example.code:
 private static CenRfgwEx _rfGateway;
 private static ClwDelvexP _dimmer;

 // Helper function to Initialize the dimmerLight object and set up all initial values
 // for the ClwDelvexP device.
 // IMPORTANT: Register your rfGateway object after you declare and initialize your device objects. 
 // Instantiation of objects after device registration can lead to issues with the device registration.
 // You can call this function in your InitializeDevice function within your program 
 // and pass your global objects into this function to initialize your ClwDelvexP device.
 public static bool InitializeDevice(CrestronControlSystem controller){
 	bool returnValue = false;
 	try {
 	    // If the controller is null, then return false right away.
 	    if(controller == null)
 	        return returnValue; 

         // If the controller does not support Ethernet, then we return false right away. 
	        if (!controller.SupportsEthernet)
		        return returnValue;

 	    // Create a new object for the rfGateway connected to the Control System. ->(this)
 	    // Device is connected the control system by an IP_ID. 
 	    // In this example we bind it to IP_ID 4.
 	    // However, yours can be on another so feel free to change this value. 
 	    // The second parameter, "this" is referring to the ControlSystem class that extends the CrestronControlSystem.
 	    // If you are not within the scope of such a class, make sure you have access to it.
 	    _rfGateway = new CenRfgwEx(0x04, controller);

 	    // Dimmer is interfaced with through the rfGateway.
 	    // To register our device on the rfGateway you need to assign the device an RF_ID.
 	    // We will assign ours to RD_ID 42, however you can change 
 	    // this to be whatever it is assigned to When device pairing occurs.
 	    // Also, the rfGateway that the device is connected to is 
 	    // needed in order to link them within the program.
 	    _dimmer = new ClwDelvexP(0x42, rf_Gateway);

 	    // Give dimmerLight a description that is indicative of the device.
 	    _dimmer.Description = "Kitchen Island Light";

 	    // Now that we have connected our devices
 	    // we can set some values for the dimmerLight.

 	    // Sets the delay time before the dimmer starts turning off.
 	    _dimmer.ParameterDimmerDelayedOffTime = 10;

 	    // Set the buttons to be remote or local
         // remote = program handles the button presses
	        // local = dimmer handles the button presses, and default behavior will be enabled.
 	    _dimmer.DimmerUISettings.ParameterButtonLogic = eButtonLogic.Remote;

 	    // Set how light will fade when turned off.
 	    _dimmer.ParameterOffFadeTime = 10;

 	    // Set how fast the light will increase in brightness.
 	    // Measured in 100ths of a second.
 	    _dimmer.ParameterPresetFadeTime = 10;

 	    // Initializes our event listeners for our dimmerLight.
 	    EventHandlerSetup(_dimmer);

 	    // Register the rfGateway with the controller.
 	    // IMPORTANT: If you want to connect another device to the gateway, you first must 
 	    // unregister the device, add the device to your gateway, and then register once again.
 	    if (_rfGateway.Register() != eDeviceRegistrationUnRegistrationResponse.Success)
 	    {
 		    ErrorLog.Error("rf-Gateway cannot be registered: {0}", rf_Gateway.RegistrationFailureReason);
 	    }   
 	    else
 	    {
 	        // If nothing went wrong during the setup, we can switch our return value to true.
 		    returnValue = true;
 	    }

     } catch(Exception e){
 	    ErrorLog.Error("Error initializing the clw-delvex device: {0}", e.Message.ToString());
     }

     // Return the status of the function execution.
     return returnValue;
 }

 // Helper function for setting up ClwDelvexP/ rfGateway event handlers. 
 private static void  EventHandlerSetup(){
    if(_rfGateway == null || _dimmer == null){
         ErrorLog.Error("Could not set up Gateway event handlers, object is null");
         throw new NullReferenceException();
         return;
     }

     // Listens for devices coming online.
 	_rfGateway.OnlineStatusChange += new OnlineStatusChangeEventHandler(Device_OnlineStatusChange);

 	// Event listeners for the dimmer.
 	_dimmer.OnlineStatusChange += new OnlineStatusChangeEventHandler(Device_OnlineStatusChange);

 	// Listen for any load changed coming to or from the dimmer.
 	_dimmer.LoadStateChange += new LoadEventHandler(dimmerLight_LoadStateChange);

 	//Listens for physical buttons on the device to be pressed.
 	_dimmer.ButtonStateChange += new ButtonEventHandler(dimmerLight_ButtonStateChange);
 }

 // Event handler for all online status changes.
 // When a device goes online or offline this event handler will listen for it.
 private static void Device_OnlineStatusChange(GenericBase currentDevice, OnlineOfflineEventArgs args)
 {
 	if (args.DeviceOnLine)
 	    ErrorLog.Notice("{0} is online", currentDevice.Description);
 	else
 		ErrorLog.Notice("{0} is offline", currentDevice.Description);
 }


 // Event triggered when the dimmerLight's load has changed.
 private static void dimmerLight_LoadStateChange(LightingBase lightingObject, LoadEventArgs args)
 {           
 	ErrorLog.Notice("Load change from Dimmer on load number: {0)", args.Load.Number);	
 }

 // Event triggered when the dimmerLight's buttons have been physically pressed.
 // This example assumes the two button face plate for the switch, not the 4 preset pace panel option.
 private static void dimmerLight_ButtonStateChange(GenericBase device, ButtonEventArgs args)
 {
 	// Top button on the dimmer switch.
 	if (args.Button.Name == eButtonName.Button1)
 	{
 		switch (args.NewButtonState)
 		{
 			// This event will always occur, so we do nothing.
 			case eButtonState.Pressed:
 			    break; 

 			// Triggered when you tap the Bottom button fast two times in a row.
 			case eButtonState.DoubleTapped: 
 			    _dimmer.DimmingLoads[1].FullOn();
 			    break;

 			// Triggered when you tap the bottom button fast once.
 			case eButtonState.Tapped: 
 	            // Will start lowering the level of the light
 	            // adhering to the values that we set previously.
 	            // Will lower the level of the light for 1000 ms ( 1 sec).  
 	            _dimmer.DimmingLoads[1].Raise.Pulse(1000);
 			    break;

 			// Triggered when the button is held longer than a normal button press ( approx on 1 sec).
 			case eButtonState.Held:
 			    _dimmer.DimmingLoads[1].FastFullOn();
 			    break;
 		}

 		ErrorLog.Notice("{0}", args.NewButtonState.ToString());
 	}

 		// Bottom button on the dimmer switch.
 		if (args.Button.Name == eButtonName.Button2)
 		{
 			switch (args.NewButtonState)
 			{
 				// This event will always occur, so we do nothing.
 				case eButtonState.Pressed:
						break; 

 				// Triggered when you tap the Bottom button fast two times in a row.
 				case eButtonState.DoubleTapped:
 					_dimmer.DimmingLoads[1].FullOff();
 					break;

 				// Triggered when you tap the bottom button fast once. When at the lowest value of the pulse, the light will stay at that level, double tap to turn off.
 				case eButtonState.Tapped:
 	                // Will start lowering the level of the light
 	                // adhering to the values that we set previously.
 	                // Will lower the level of the light for 1000 ms ( 1 sec).  
 	                _dimmer.DimmingLoads[1].Lower.Pulse(1000);					
 					break;

 				// Triggered when the button is held longer than a normal button press ( approx on 1 sec).
 				case eButtonState.Held: 
 					_dimmer.DimmingLoads[1].FastFullOff();
 					break;
 			}
 		}
 }

// Register the device within the constructor or InitializeSystem function
public ControlSystem() : base()
{
	myDin1Dim4 = new Din1Dim4(0x89, this);
	// Now register the event handlers
	myDin1Dim4.OverrideEventHandler += new OverrideHandler(OverrideEventHandler);
	myDin1Dim4.OnlineStatusChange += new OnlineStatusChangeEventHandler(OnlineStatusChangeCallback);
	myDin1Dim4.BaseEvent += new BaseEventHandler(BaseEventCallback);

	// Need to set parameters before we register the device
	foreach (DinDimmableLoad dimmableLoad in myDin1Dim4.DinLoads)
	{
		dimmableLoad.ParameterOverrideLevel = SimplSharpDeviceHelper.PercentToUshort(50);
	}
	// Register the device now
	if (myDin1Dim4.Register() != eDeviceRegistrationUnRegistrationResponse.Success)
	{
		CrestronConsole.PrintLine(" Unable to register for device ");
	}
	else
	{
		CrestronConsole.PrintLine(" Device successfully registered ");
	}
}

// Online Offline Status Change Event Handler 
void OnlineStatusChangeCallback(GenericBase currentDevice, OnlineOfflineEventArgs args)
{
	CrestronConsole.PrintLine(" Din1Dim4 state {0} ", args.DeviceOnLine ? "Online" : "Offline");
}

// Base Event handler for the Din1Dim4
void BaseEventCallback(GenericBase device, BaseEventArgs args)
{
	switch (args.EventId)
	{
		case DinLoadBaseClass.LevelOutFeedbackEventId:
			CrestronConsole.PrintLine(" Level out changed for output {0} ", args.Index);
			CrestronConsole.PrintLine(" Value of load is {0} ", myDin1Dim4.AnalogOutputs[(uint)args.Index].LevelOut.UShortValue);
			break;
		case Crestron.DeviceSupport.Lighting.DinXXXLoadBaseClass.NoAcPowerFeedbackEventId:
            CrestronConsole.PrintLine(" NoAcPowerFeedbackEventId fired. State is {0} ",myDin1Dim4.NoAcPower.BoolValue);
            break; 
	}
}

// Override handler for the Din1Dim4
void OverrideEventHandler(GenericBase device, eOverrideEvents overrideEvent)
{
	switch (overrideEvent)
	{
		case eOverrideEvents.External:
			CrestronConsole.PrintLine(" Device  reports external override event. State is {0} ", myDin1Dim4.InExternalOverrideFeedback.BoolValue);
			break;
		case eOverrideEvents.Manual:
			CrestronConsole.PrintLine(" Device  reports manual override event. State is {0} ", myDin1Dim4.InManualOverrideFeedback.BoolValue);
			break;
	}
}

