	myDin1DimU4.OnlineStatusChange += new OnlineStatusChangeEventHandler(OnlineStatusChangeCallback);
	myDin1DimU4.BaseEvent += new BaseEventHandler(BaseEventCallback);

	// Need to set parameters before we register the device
	foreach (DinDimmableLoad dimmableLoad in myDin1DimU4.DinLoads)
	{
		dimmableLoad.ParameterOverrideLevel = SimplSharpDeviceHelper.PercentToUshort(50);
	}
	// Register the device now
	if (myDin1DimU4.Register() != eDeviceRegistrationUnRegistrationResponse.Success)
	{
		CrestronConsole.PrintLine(" Unable to register for device ");
	}
	else
	{
		CrestronConsole.PrintLine(" Device successfully registered ");
	}
}

